// ðŸ“‚ src/components/Assessments/RuntimeForm.jsx
import React from "react";

export default function RuntimeForm({ assessment }) {
  if (!assessment) return null;

  return (
    <form style={{ border: "1px solid #ddd", padding: "15px" }}>
      <h3>{assessment.title}</h3>

      {assessment.sections.map((section, sIdx) => (
        <div key={sIdx}>
          <h4>{section.name}</h4>
          {section.questions.map((q, qIdx) => (
            <div key={qIdx} style={{ marginBottom: "10px" }}>
              <label>{q.text}</label>
              {q.type === "short" && <input type="text" />}
              {q.type === "long" && <textarea rows="3"></textarea>}
              {q.type === "numeric" && <input type="number" />}
              {q.type === "multi" &&
                q.options?.map((opt, i) => (
                  <div key={i}>
                    <label>
                      <input type="checkbox" /> {opt}
                    </label>
                  </div>
                ))}
              {q.type === "file" && <input type="file" />}
            </div>
          ))}
        </div>
      ))}
    </form>
  );
}
