import React, { useEffect, useState } from "react";
import Preview from "./Preview";
import { useParams } from "react-router-dom";

export default function Builder() {
  const { jobId } = useParams();
  const [job, setJob] = useState(jobId ? Number(jobId) : null);

  const [assessment, setAssessment] = useState({
    title: "New Assessment",
    sections: [{ title: "Section 1", questions: [] }],
  });

  const [selectedSection, setSelectedSection] = useState(0);

  // Load assessment data
  useEffect(() => {
    async function load() {
      if (!job) return;
      try {
        const res = await fetch(`/assessments/${job}`);
        if (!res.ok) throw new Error("Fetch failed");
        const data = await res.json();
        if (data) setAssessment(data);
      } catch (err) {
        console.warn("No assessment found for this job, using default template.");
      }
    }
    load();
  }, [job]);

  // Add a new question
  const addQuestion = (type = "short") => {
    const newQ = {
      id: "q" + Date.now(),
      type,
      label: `New ${type} question`,
      required: false,
      options: type.includes("choice") ? ["Option 1", "Option 2"] : [],
    };

    setAssessment((prev) => {
      const clone = { ...prev };
      clone.sections[selectedSection].questions.push(newQ);
      return { ...clone };
    });
  };

  // Add a new section
  const addSection = () => {
    setAssessment((prev) => ({
      ...prev,
      sections: [...prev.sections, { title: "New Section", questions: [] }],
    }));
    setSelectedSection(assessment.sections.length); // auto-select new one
  };

  // Save to backend
  const save = async () => {
    if (!job) return alert("Add jobId in URL like /assessments/1");
    try {
      await fetch(`/assessments/${job}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(assessment),
      });
      alert("‚úÖ Assessment saved successfully!");
    } catch {
      alert("‚ùå Failed to save assessment.");
    }
  };

  return (
    <div className="flex gap-6 p-4">
      {/* Left Panel */}
      <div className="flex-1 border rounded-2xl p-4 shadow-md bg-white">
        <h2 className="text-xl font-semibold mb-3">
          Builder ‚Äì {assessment.title}
        </h2>

        {/* Title Input */}
        <div className="mb-4">
          <label className="block text-sm font-medium">Assessment Title</label>
          <input
            value={assessment.title}
            onChange={(e) =>
              setAssessment({ ...assessment, title: e.target.value })
            }
            className="border p-2 rounded w-full mt-1"
          />
        </div>

        {/* Buttons */}
        <div className="flex flex-wrap gap-2 mb-4">
          <button
            onClick={addSection}
            className="bg-blue-500 text-white px-3 py-1 rounded"
          >
            + Add Section
          </button>

          <div className="relative">
            <button className="bg-green-500 text-white px-3 py-1 rounded">
              + Add Question ‚ñº
            </button>
            <div className="absolute bg-white border mt-1 rounded shadow-md z-10 hidden group-hover:block">
              {["short", "long", "single", "multi", "numeric", "file"].map(
                (t) => (
                  <button
                    key={t}
                    onClick={() => addQuestion(t)}
                    className="block w-full text-left px-3 py-1 hover:bg-gray-100"
                  >
                    {t}
                  </button>
                )
              )}
            </div>
          </div>

          <button
            onClick={save}
            className="bg-indigo-600 text-white px-3 py-1 rounded"
          >
            üíæ Save
          </button>
        </div>

        {/* Section List */}
        {assessment.sections.map((s, idx) => (
          <div
            key={idx}
            className={`border rounded-lg p-3 mb-3 ${
              selectedSection === idx ? "bg-blue-50" : ""
            }`}
          >
            <div className="flex justify-between items-center mb-2">
              <input
                value={s.title}
                onChange={(e) => {
                  const clone = { ...assessment };
                  clone.sections[idx].title = e.target.value;
                  setAssessment({ ...clone });
                }}
                className="border p-1 rounded w-3/4"
              />
              <button
                onClick={() => setSelectedSection(idx)}
                className="text-sm text-blue-600"
              >
                ‚úèÔ∏è Edit
              </button>
            </div>

            {/* Question List */}
            {s.questions.length === 0 ? (
              <p className="text-gray-400 text-sm">No questions yet.</p>
            ) : (
              s.questions.map((q, qi) => (
                <div
                  key={q.id}
                  className="border p-2 rounded mb-2 bg-gray-50 text-sm"
                >
                  <input
                    value={q.label}
                    onChange={(e) => {
                      const clone = { ...assessment };
                      clone.sections[idx].questions[qi].label = e.target.value;
                      setAssessment({ ...clone });
                    }}
                    className="border p-1 rounded w-full mb-1"
                  />
                  <select
                    value={q.type}
                    onChange={(e) => {
                      const clone = { ...assessment };
                      clone.sections[idx].questions[qi].type = e.target.value;
                      setAssessment({ ...clone });
                    }}
                    className="border p-1 rounded w-full"
                  >
                    <option value="short">Short Text</option>
                    <option value="long">Long Text</option>
                    <option value="single">Single Choice</option>
                    <option value="multi">Multi Choice</option>
                    <option value="numeric">Numeric</option>
                    <option value="file">File Upload</option>
                  </select>
                </div>
              ))
            )}
          </div>
        ))}
      </div>

      {/* Right Panel */}
      <div className="w-[420px] border rounded-2xl shadow-md bg-gray-50 p-4">
        <Preview assessment={assessment} onChange={setAssessment} />
      </div>
    </div>
  );
}
