import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

// âœ… Correct file name â€” must match exactly with your actual file
import JobBoard from "./components/Jobs/JobsBoard.jsx";
import JobDetail from "./components/Jobs/JobDetail";
import CandidatesList from "./components/Candidates/CandidatesList";
import CandidateProfile from "./components/Candidates/CandidateProfile";
import Builder from "./components/Assessments/Builder";

export default function App() {
  return (
    <>
      <div className="container" style={{ padding: 20 }}>
        {/* ðŸŒŸ Header */}
        <header
          className="header"
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 20,
          }}
        >
          <h1 style={{ margin: 0 }}>TalentFlow</h1>
          <nav style={{ display: "flex", gap: "12px" }}>
            <Link to="/jobs">Jobs</Link>
            <Link to="/candidates">Candidates</Link>
            <Link to="/assessments">Assessments</Link>
          </nav>
        </header>

        {/* ðŸ”— Routes */}
        <Routes>
          <Route path="/" element={<JobBoard />} />
          <Route path="/jobs" element={<JobBoard />} />
          <Route path="/jobs/:jobId" element={<JobDetail />} />

          <Route path="/candidates" element={<CandidatesList />} />
          <Route path="/candidates/:id" element={<CandidateProfile />} />

          <Route path="/assessments" element={<Builder />} />
          <Route path="/assessments/:jobId" element={<Builder />} />
        </Routes>
      </div>
    </>
  );
}
