import React, { useState } from "react";
import { useJobs, useReorderJob } from "../../hooks/useApi";
import JobCard from "./JobCard";

import { db, seedIfNeeded } from "../../hooks/db";
import { useEffect } from "react";

useEffect(() => {
  seedIfNeeded(); // ✅ ensure 25 jobs are loaded once
}, []);



import {
  DndContext,
  closestCenter,
} from "@dnd-kit/core";
import {
  SortableContext,
  verticalListSortingStrategy,
  arrayMove,
} from "@dnd-kit/sortable";
import SortableJob from "./SortableJob";

export default function JobBoard() {
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState("");
  const [status, setStatus] = useState("");
  const [tags, setTags] = useState("");
  const [localJobs, setLocalJobs] = useState([]); // 👈 for new added jobs
  const pageSize = 10;
   const [jobs, setJobs] = useState([]);
   const loadJobs = async () => {
    const data = await db.jobs.toArray();
    setJobs(data);
  };
   useEffect(() => {
    loadJobs();
  }, []);

  const { data, isLoading, error } = useJobs({
    search,
    status,
    tags,
    page,
    pageSize,
  });

  const reorderJob = useReorderJob();
  const items = [...(data?.items || []), ...localJobs]; // 👈 combine API + new ones
  const total = (data?.total || 0) + localJobs.length;

  // Drag handling
  const handleDragEnd = (event) => {
    const { active, over } = event;
    if (!over || active.id === over.id) return;
    const oldIndex = items.findIndex((i) => i.id === active.id);
    const newIndex = items.findIndex((i) => i.id === over.id);
    arrayMove(items, oldIndex, newIndex);
    reorderJob.mutate({
      id: active.id,
      fromOrder: oldIndex + 1,
      toOrder: newIndex + 1,
    });
  };

  // ➕ Add new job
  const handleAddJob = () => {
    const title = prompt("Enter new job title:");
    if (!title) return;

    const newJob = {
      id: Date.now(), // unique id
      title: title,
      description: "Newly added job",
      status: "active",
    };

    setLocalJobs((prev) => [...prev, newJob]); // add in list
  };

  if (isLoading) return <div>Loading jobs...</div>;
  if (error) return <div>Error loading jobs</div>;

  return (
    
    <div style={{ padding: 20 }}>
      {/* 🔍 Filters */}
      <div
        style={{
          display: "flex",
          gap: 10,
          marginBottom: 16,
          alignItems: "center",
        }}
        
      >
        <input
          placeholder="Search title"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: 6, flex: 1 }}
        />

        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{ padding: 6 }}
        >
          <option value="">All</option>
          <option value="active">Active</option>
          <option value="archived">Archived</option>
        </select>

        <input
          placeholder="Tags (comma separated)"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          style={{ padding: 6, flex: 1 }}
        />
        

        <button
          style={{
            padding: "6px 12px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: 4,
            cursor: "pointer",
          }}
          onClick={handleAddJob} // 👈 updated
        >
          + New Job
        </button>
      </div>

      {/* 🧾 Job List */}
      <DndContext collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext
          items={items.map((i) => i.id)}
          strategy={verticalListSortingStrategy}
        >
          {items.length === 0 ? (
            <div>No jobs found.</div>
          ) : (
            items.map((job) => (
              <SortableJob key={job.id} id={job.id} job={job} />
            ))
          )}
        </SortableContext>
      </DndContext>

      {/* ⏩ Pagination */}
      <div style={{ marginTop: 20, textAlign: "center" }}>
        <button
          disabled={page <= 1}
          onClick={() => setPage((p) => p - 1)}
          style={{ padding: "6px 10px", marginRight: 10 }}
        >
          Prev
        </button>

        <span>
          Page {page} / {Math.ceil(total / pageSize) || 1}
        </span>

        <button
          disabled={page * pageSize >= total}
          onClick={() => setPage((p) => p + 1)}
          style={{ padding: "6px 10px", marginLeft: 10 }}
        >
          Next
        </button>
      </div>
    </div>
  );
}
