import React from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function RuntimeForm() {
  const { assessments } = useAssessmentStore();

  // ‚úÖ If no assessments found
  if (!assessments || assessments.length === 0) {
    return (
      <p
        style={{
          padding: "20px",
          fontFamily: "Poppins, sans-serif",
          color: "#666",
        }}
      >
        ‚ö†Ô∏è No assessments available yet.
      </p>
    );
  }

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: 22, fontWeight: 600, marginBottom: 20 }}>
        üìã Live Preview (Fillable Form)
      </h2>

      {assessments.map((a) => (
        <form
          key={a.id}
          style={{
            border: "1px solid #ddd",
            borderRadius: "12px",
            padding: "20px",
            marginBottom: "25px",
            background: "#f9f9f9",
            boxShadow: "0 2px 5px rgba(0,0,0,0.05)",
          }}
        >
          <h3 style={{ color: "#007bff", marginBottom: "10px" }}>{a.title}</h3>

          {(a.sections || []).map((section, sIndex) => (
            <div
              key={sIndex}
              style={{
                marginBottom: "15px",
                border: "1px solid #e0e0e0",
                padding: "12px",
                borderRadius: "8px",
                background: "#fff",
              }}
            >
              <h4 style={{ fontWeight: 600, marginBottom: "8px" }}>
                {section.name}
              </h4>

              {(section.questions || []).map((q, qIndex) => (
                <div key={qIndex} style={{ marginBottom: "12px" }}>
                  <label
                    style={{
                      display: "block",
                      marginBottom: "6px",
                      fontWeight: 500,
                    }}
                  >
                    {q.text}
                  </label>

                  {/* üü¢ Dynamic Input Types */}
                  {q.type === "short" && (
                    <input
                      type="text"
                      placeholder="Type your answer..."
                      style={{
                        width: "100%",
                        padding: "8px",
                        borderRadius: "6px",
                        border: "1px solid #ccc",
                      }}
                    />
                  )}

                  {q.type === "long" && (
                    <textarea
                      placeholder="Type your detailed answer..."
                      style={{
                        width: "100%",
                        padding: "8px",
                        borderRadius: "6px",
                        border: "1px solid #ccc",
                      }}
                      rows="3"
                    />
                  )}

                  {q.type === "numeric" && (
                    <input
                      type="number"
                      min={q.min || 0}
                      max={q.max || 100}
                      placeholder="Enter number..."
                      style={{
                        width: "100%",
                        padding: "8px",
                        borderRadius: "6px",
                        border: "1px solid #ccc",
                      }}
                    />
                  )}

                  {q.type === "single" &&
                    (q.options || []).map((opt, i) => (
                      <div key={i}>
                        <label>
                          <input
                            type="radio"
                            name={`${a.id}-${section.name}-${qIndex}`}
                            value={opt}
                            style={{ marginRight: "6px" }}
                          />
                          {opt}
                        </label>
                      </div>
                    ))}

                  {q.type === "multi" &&
                    (q.options || []).map((opt, i) => (
                      <div key={i}>
                        <label>
                          <input
                            type="checkbox"
                            value={opt}
                            style={{ marginRight: "6px" }}
                          />
                          {opt}
                        </label>
                      </div>
                    ))}
                </div>
              ))}
            </div>
          ))}
        </form>
      ))}
    </div>
  );
}
