import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./us";
import Preview from "./Preview";
import RuntimeForm from "./RuntimeForm";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment, updateAssessment } =
    useAssessmentStore();
  const [expandedId, setExpandedId] = useState(null);
  const [showPreview, setShowPreview] = useState(false);

  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  // Add 3 default assessments with 12 questions (only once)
  useEffect(() => {
    if (assessments.length === 0) {
      const sampleQuestions = Array.from({ length: 12 }, (_, i) => ({
        id: i + 1,
        question: `Sample Question ${i + 1}?`,
      }));

      const sampleData = [
        { title: "Assessment 1", sections: [{ name: "Section 1", questions: sampleQuestions }] },
        { title: "Assessment 2", sections: [{ name: "Section 1", questions: sampleQuestions }] },
        { title: "Assessment 3", sections: [{ name: "Section 1", questions: sampleQuestions }] },
      ];
      sampleData.forEach((a) => addAssessment(a));
    }
  }, [assessments, addAssessment]);

  const toggleExpand = (id) =>
    setExpandedId((prev) => (prev === id ? null : id));

  return (
    <div style={{ display: "flex", gap: "20px", padding: "20px" }}>
      {/* Left pane - Assessments */}
      <div style={{ flex: 1 }}>
        <h2>ðŸ§© Assessment Builder</h2>
        {assessments.map((a) => (
          <div
            key={a.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "10px",
              margin: "10px 0",
              padding: "10px",
            }}
          >
            <div
              onClick={() => toggleExpand(a.id)}
              style={{
                cursor: "pointer",
                color: "#007bff",
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              <span>{a.title}</span>
              <span>{expandedId === a.id ? "â–²" : "â–¼"}</span>
            </div>

            {expandedId === a.id && (
              <div style={{ marginTop: "10px" }}>
                {a.sections?.map((s, idx) => (
                  <div key={idx}>
                    <h4>{s.name}</h4>
                    <ul>
                      {s.questions.map((q) => (
                        <li key={q.id}>{q.question}</li>
                      ))}
                    </ul>
                  </div>
                ))}
                <button
                  onClick={() => setShowPreview(true)}
                  style={{
                    background: "#007bff",
                    color: "white",
                    padding: "5px 10px",
                    border: "none",
                    borderRadius: "5px",
                  }}
                >
                  Preview Form
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Right Pane - Preview */}
      <div style={{ flex: 1 }}>
        {showPreview && (
          <Preview onClose={() => setShowPreview(false)}>
            <RuntimeForm />
          </Preview>
        )}
      </div>
    </div>
  );
}
