import React, { useState, useEffect } from "react";
import Dexie from "dexie";
import AssessmentPreview from "./Preview";

// âœ… Create local IndexedDB
const db = new Dexie("TalentFlowDB");
db.version(1).stores({
  assessments: "++id, title, sections",
});

// âš™ï¸ Simulate latency & 5â€“10% random error
const simulateNetwork = async () => {
  const delay = Math.random() * (1200 - 200) + 200;
  await new Promise((res) => setTimeout(res, delay));
  if (Math.random() < 0.1) throw new Error("Network error! Try again.");
};

export default function Builder() {
  const [assessment, setAssessment] = useState({
    title: "New Assessment",
    sections: [{ id: Date.now(), name: "Section 1", questions: [] }],
  });
  const [selectedSection, setSelectedSection] = useState(0);
  const [sectionName, setSectionName] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  // ðŸ§© Load data from IndexedDB on mount
  useEffect(() => {
    const load = async () => {
      const data = await db.assessments.toArray();
      if (data.length > 0) setAssessment(data[data.length - 1]);
    };
    load();
  }, []);

  // ðŸ’¾ Auto-save to IndexedDB whenever assessment changes
  useEffect(() => {
    const saveToDB = async () => {
      try {
        setStatus("Saving...");
        await simulateNetwork();
        await db.assessments.put(assessment);
        setStatus("âœ… Saved locally");
      } catch (err) {
        setError(err.message);
      } finally {
        setTimeout(() => setStatus(""), 2000);
      }
    };
    saveToDB();
  }, [assessment]);

  // âž• Add new section
  const addSection = () => {
    if (!sectionName.trim()) return alert("Please enter a section name!");
    setAssessment((prev) => ({
      ...prev,
      sections: [
        ...prev.sections,
        { id: Date.now(), name: sectionName, questions: [] },
      ],
    }));
    setSectionName("");
  };

  // âž• Add question
  const addQuestion = (type) => {
    const q = {
      id: "q" + Date.now(),
      type,
      label: "New " + type + " question",
      required: false,
      maxLength: type === "long" ? 500 : 100,
      range: type === "numeric" ? { min: 0, max: 100 } : null,
      options: ["Option 1", "Option 2"],
      condition: { dependsOn: "", value: "" },
    };

    const clone = { ...assessment };
    clone.sections[selectedSection].questions.push(q);
    setAssessment(clone);
  };

  // âœï¸ Update question
  const updateQuestion = (secIdx, qIdx, field, value) => {
    const clone = { ...assessment };
    clone.sections[secIdx].questions[qIdx][field] = value;
    setAssessment(clone);
  };

  return (
    <div className="flex flex-col md:flex-row gap-6 p-6">
      {/* Left panel â€“ builder */}
      <div className="md:w-1/2 bg-white rounded-2xl shadow-lg p-5">
        <div className="flex justify-between items-center mb-3">
          <h2 className="text-2xl font-bold">ðŸ§© Assessment Builder</h2>
          {status && <span className="text-sm text-green-600">{status}</span>}
        </div>
        {error && <p className="text-red-600">{error}</p>}

        {/* Assessment Title */}
        <label className="block text-gray-600 mb-1">Assessment Title</label>
        <input
          className="border p-2 rounded w-full mb-4"
          value={assessment.title}
          onChange={(e) =>
            setAssessment({ ...assessment, title: e.target.value })
          }
        />

        {/* Add Section */}
        <div className="flex gap-2 mb-4">
          <input
            placeholder="New section name"
            className="border p-2 rounded w-full"
            value={sectionName}
            onChange={(e) => setSectionName(e.target.value)}
          />
          <button
            onClick={addSection}
            className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
          >
            âž• Add Section
          </button>
        </div>

        {/* Section List */}
        <div className="space-y-2">
          {assessment.sections.map((s, idx) => (
            <div
              key={s.id}
              onClick={() => setSelectedSection(idx)}
              className={`border rounded p-3 cursor-pointer transition ${
                selectedSection === idx
                  ? "bg-blue-100 border-blue-400"
                  : "bg-gray-50 hover:bg-gray-100"
              }`}
            >
              <div className="font-semibold">{s.name}</div>
              <div className="text-sm text-gray-500">
                {s.questions.length} Questions
              </div>
            </div>
          ))}
        </div>

        {/* Question types */}
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Add Question Type</h3>
          {[
            "short",
            "long",
            "single-choice",
            "multi-choice",
            "numeric",
            "file",
          ].map((t) => (
            <button
              key={t}
              onClick={() => addQuestion(t)}
              className="border p-1 px-2 m-1 rounded hover:bg-gray-100"
            >
              {t}
            </button>
          ))}
        </div>

        {/* Question editor */}
        {assessment.sections[selectedSection]?.questions.length > 0 && (
          <div className="mt-4 border-t pt-3">
            <h3 className="font-semibold mb-2">Edit Questions</h3>
            {assessment.sections[selectedSection].questions.map((q, qi) => (
              <div
                key={q.id}
                className="border p-2 mb-2 rounded bg-gray-50 space-y-1"
              >
                {/* Label */}
                <input
                  className="border p-1 w-full rounded"
                  value={q.label}
                  onChange={(e) =>
                    updateQuestion(selectedSection, qi, "label", e.target.value)
                  }
                />

                {/* Options */}
                {q.type.includes("choice") && (
                  <textarea
                    className="border p-1 w-full rounded text-sm"
                    rows={2}
                    value={q.options.join("\n")}
                    onChange={(e) =>
                      updateQuestion(
                        selectedSection,
                        qi,
                        "options",
                        e.target.value.split("\n")
                      )
                    }
                  />
                )}

                {/* Numeric range */}
                {q.type === "numeric" && (
                  <div className="flex gap-2">
                    <input
                      type="number"
                      className="border p-1 rounded w-1/2"
                      value={q.range.min}
                      onChange={(e) =>
                        updateQuestion(selectedSection, qi, "range", {
                          ...q.range,
                          min: +e.target.value,
                        })
                      }
                      placeholder="Min"
                    />
                    <input
                      type="number"
                      className="border p-1 rounded w-1/2"
                      value={q.range.max}
                      onChange={(e) =>
                        updateQuestion(selectedSection, qi, "range", {
                          ...q.range,
                          max: +e.target.value,
                        })
                      }
                      placeholder="Max"
                    />
                  </div>
                )}

                {/* Required / Max length */}
                <div className="flex gap-2 items-center">
                  <label className="text-sm">
                    <input
                      type="checkbox"
                      checked={q.required}
                      onChange={(e) =>
                        updateQuestion(
                          selectedSection,
                          qi,
                          "required",
                          e.target.checked
                        )
                      }
                    />{" "}
                    Required
                  </label>
                  {q.type === "long" && (
                    <input
                      type="number"
                      className="border p-1 w-24 rounded"
                      value={q.maxLength}
                      onChange={(e) =>
                        updateQuestion(
                          selectedSection,
                          qi,
                          "maxLength",
                          +e.target.value
                        )
                      }
                      placeholder="Max length"
                    />
                  )}
                </div>

                {/* Conditional logic */}
                <div className="text-sm">
                  <label>Show only if question ID = </label>
                  <input
                    className="border p-1 w-24 rounded"
                    value={q.condition.dependsOn}
                    onChange={(e) =>
                      updateQuestion(selectedSection, qi, "condition", {
                        ...q.condition,
                        dependsOn: e.target.value,
                      })
                    }
                  />
                  <label> equals </label>
                  <input
                    className="border p-1 w-24 rounded"
                    value={q.condition.value}
                    onChange={(e) =>
                      updateQuestion(selectedSection, qi, "condition", {
                        ...q.condition,
                        value: e.target.value,
                      })
                    }
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Right panel â€“ live preview */}
      <div className="md:w-1/2 bg-gray-50 rounded-2xl shadow-lg p-5">
        <AssessmentPreview assessment={assessment} />
      </div>
    </div>
  );
}
