import React, { useEffect, useState } from "react";
import Dexie from "dexie";
import { useAssessmentStore } from './useAssessmentStore';

const db = new Dexie("TalentFlowDB");
db.version(1).stores({
  assessments: "++id,title,sections",
});

export default function Builder() {
  const [expandedId, setExpandedId] = useState(null);
  const { assessments, fetchAssessments, setSelectedAssessment } = useAssessmentStore();

  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: "24px", fontWeight: "600", marginBottom: "20px" }}>
        ðŸ§© Assessment Builder
      </h2>

      {assessments.map((a) => (
        <div
          key={a.id}
          onClick={() => toggleExpand(a.id)}
          style={{
            background: "#f5f6f8",
            marginBottom: "15px",
            padding: "20px",
            borderRadius: "12px",
            boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
            cursor: "pointer",
          }}
        >
          <h3
            style={{
              color: "#007bff",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              margin: 0,
            }}
          >
            {a.title} <span>{expandedId === a.id ? "â–²" : "â–¼"}</span>
          </h3>

          {expandedId === a.id && (
            <div style={{ marginTop: "15px", paddingLeft: "10px" }}>
              {(a.sections || []).map((section, i) => (
                <div key={i} style={{ marginBottom: "10px" }}>
                  <h4 style={{ margin: "8px 0", fontWeight: "600" }}>{section.name}</h4>
                  <ul style={{ paddingLeft: "20px", lineHeight: "1.8" }}>
                    {(section.questions || []).map((q, idx) => (
                      <li key={idx}>
                        {q.text}{" "}
                        <small style={{ color: "#777" }}>
                          ({q.type}
                          {q.options ? ` â€¢ ${q.options.length} options` : ""})
                        </small>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
              <button
                style={{
                  background: "#007bff",
                  color: "white",
                  border: "none",
                  borderRadius: "8px",
                  padding: "8px 14px",
                  cursor: "pointer",
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  setSelectedAssessment(a);
                  alert(`Opening modal for ${a.title}`);
                }}
              >
                View in Modal
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
