import React from "react";

export default function AssessmentPreview({ assessment }) {
  return (
    <div>
      <h3 className="text-lg font-semibold mb-2">ðŸ“‹ Live Preview</h3>
      <p className="text-gray-600 mb-3">{assessment.title}</p>

      {assessment.sections.map((s) => (
        <div key={s.id} className="mb-4">
          <h4 className="font-semibold mb-2">{s.name}</h4>
          {s.questions.map((q) => (
            <div key={q.id} className="mb-3">
              <label className="block mb-1 font-medium">
                {q.label} {q.required && <span className="text-red-500">*</span>}
              </label>

              {q.type === "short" && <input className="border p-2 w-full rounded" />}
              {q.type === "long" && (
                <textarea className="border p-2 w-full rounded"></textarea>
              )}
              {q.type === "numeric" && (
                <input type="number" className="border p-2 w-full rounded" />
              )}
              {q.type === "file" && <input type="file" className="border" />}

              {q.type === "single-choice" &&
                q.options.map((opt, i) => (
                  <div key={i}>
                    <input type="radio" name={q.id} /> {opt}
                  </div>
                ))}

              {q.type === "multi-choice" &&
                q.options.map((opt, i) => (
                  <div key={i}>
                    <input type="checkbox" /> {opt}
                  </div>
                ))}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
