// ðŸ“‚ src/components/Assessments/Builder.jsx
import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./useAssessmentStoreuseAssessmentStore";
import RuntimeForm from "./RuntimeForm";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment, addQuestion } =
    useAssessmentStore();
  const [selectedAssessment, setSelectedAssessment] = useState(null);
  const [newTitle, setNewTitle] = useState("");

  useEffect(() => {
    fetchAssessments(); // âœ… properly called
  }, [fetchAssessments]);

  const handleAddAssessment = () => {
    if (!newTitle.trim()) return;
    addAssessment(newTitle);
    setNewTitle("");
  };

  const handleAddQuestion = () => {
    if (!selectedAssessment) return alert("Select an assessment first!");
    const newQuestion = {
      text: "New custom question",
      type: "short",
      options: [],
    };
    addQuestion(selectedAssessment.id, "Section 1", newQuestion);
  };

  return (
    <div style={{ display: "flex", gap: "20px", padding: "20px" }}>
      {/* Left Panel */}
      <div style={{ flex: 1 }}>
        <h2>Assessment Builder</h2>
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="New Assessment Title"
          />
          <button onClick={handleAddAssessment}>Add</button>
        </div>

        <ul>
          {assessments.map((a) => (
            <li key={a.id}>
              <button onClick={() => setSelectedAssessment(a)}>
                {a.title}
              </button>
            </li>
          ))}
        </ul>

        {selectedAssessment && (
          <button onClick={handleAddQuestion}>+ Add Question</button>
        )}
      </div>

      {/* Right Panel */}
      <div style={{ flex: 1, background: "#fafafa", padding: "20px" }}>
        <h3>Live Preview</h3>
        {selectedAssessment ? (
          <RuntimeForm assessment={selectedAssessment} />
        ) : (
          <p>Select an assessment to preview</p>
        )}
      </div>
    </div>
  );
}
