import React, { useEffect, useState } from "react";
import Dexie from "dexie";

// âœ… Create or open IndexedDB
const db = new Dexie("TalentFlowDB");
db.version(1).stores({
  assessments: "++id,title,sections",
});

// âœ… Seed sample data (only once)
const seedData = async () => {
  const count = await db.assessments.count();
  if (count === 0) {
    const sampleAssessments = [
      {
        title: "Assessment 1",
        sections: [
          {
            name: "General Knowledge",
            questions: [
              { type: "short", text: "What is React?" },
              { type: "long", text: "Explain Virtual DOM." },
              {
                type: "single",
                text: "Which hook manages state?",
                options: ["useState", "useMemo", "useRef"],
              },
              {
                type: "multi",
                text: "Select frontend frameworks:",
                options: ["React", "Angular", "Vue"],
              },
              { type: "numeric", text: "Years of experience?", min: 0, max: 10 },
            ],
          },
        ],
      },
      {
        title: "Assessment 2",
        sections: [
          {
            name: "Backend Concepts",
            questions: [
              { type: "short", text: "What is an API?" },
              { type: "long", text: "Explain REST vs GraphQL." },
              {
                type: "single",
                text: "Node.js is ___?",
                options: ["Runtime", "Framework", "Language"],
              },
              {
                type: "multi",
                text: "Select databases:",
                options: ["MySQL", "MongoDB", "Postgres"],
              },
              { type: "numeric", text: "Rate Node.js skill (1â€“10)", min: 1, max: 10 },
            ],
          },
        ],
      },
      {
        title: "Assessment 3",
        sections: [
          {
            name: "UI/UX Design",
            questions: [
              { type: "short", text: "What is UI design?" },
              { type: "long", text: "Explain color psychology." },
              {
                type: "single",
                text: "Which is a vector tool?",
                options: ["Figma", "Photoshop", "MS Paint"],
              },
              {
                type: "multi",
                text: "Select UX methods:",
                options: ["Wireframing", "Prototyping", "Testing"],
              },
              { type: "numeric", text: "Years of experience?", min: 0, max: 15 },
            ],
          },
        ],
      },
    ];
    await db.assessments.bulkAdd(sampleAssessments);
    console.log("âœ… 3 Assessments seeded successfully!");
  }
};

export default function Builder() {
  const [assessments, setAssessments] = useState([]);
  const [selectedAssessment, setSelectedAssessment] = useState(null);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      await seedData();
      const data = await db.assessments.toArray();
      setAssessments(data || []);
    };
    loadData();
  }, []);

  // âœ… Toggle dropdown open/close
  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: "24px", fontWeight: "600", marginBottom: "20px" }}>
        ðŸ§© Assessment Builder
      </h2>

      {assessments.map((a) => (
        <div
          key={a.id}
          style={{
            background: "#f5f7f9",
            marginBottom: "15px",
            padding: "18px 20px",
            borderRadius: "12px",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            transition: "0.3s",
          }}
        >
          {/* Title Row */}
          <div
            onClick={() => toggleExpand(a.id)}
            style={{
              cursor: "pointer",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              color: "#007bff",
              fontWeight: "600",
            }}
          >
            <span>{a.title}</span>
            <span>{expandedId === a.id ? "â–²" : "â–¼"}</span>
          </div>

          {/* Dropdown Content */}
          {expandedId === a.id && (
            <div style={{ marginTop: "15px" }}>
              {(a.sections || []).map((section, i) => (
                <div key={i} style={{ marginBottom: "15px" }}>
                  <h4 style={{ marginBottom: "8px", color: "#333" }}>
                    {section.name}
                  </h4>
                  <ul style={{ paddingLeft: "20px", lineHeight: "1.8" }}>
                    {(section.questions || []).map((q, idx) => (
                      <li key={idx}>
                        {q.text}{" "}
                        <small style={{ color: "#666" }}>
                          ({q.type}
                          {q.options ? ` â€¢ ${q.options.length} options` : ""})
                        </small>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}

      {/* âœ… Modal (optional view) */}
      {selectedAssessment && (
        <div
          onClick={() => setSelectedAssessment(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()}
            style={{
              background: "#fff",
              padding: "25px",
              borderRadius: "12px",
              width: "600px",
              maxHeight: "80vh",
              overflowY: "auto",
            }}
          >
            <h3 style={{ marginBottom: "10px" }}>{selectedAssessment.title}</h3>
            {(selectedAssessment.sections || []).map((section, i) => (
              <div key={i} style={{ marginBottom: "20px" }}>
                <h4>{section.name}</h4>
                <ul style={{ paddingLeft: "20px" }}>
                  {(section.questions || []).map((q, idx) => (
                    <li key={idx}>{q.text}</li>
                  ))}
                </ul>
              </div>
            ))}
            <button
              onClick={() => setSelectedAssessment(null)}
              style={{
                background: "#007bff",
                color: "#fff",
                border: "none",
                padding: "10px 16px",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
