import React, { useState } from "react";
import {
  DndContext,
  PointerSensor,
  useSensor,
  useSensors,
  closestCenter,
} from "@dnd-kit/core";
import KanbanColumn from "./KanbanColumn";

export default function KanbanBoard() {
  const stages = ["tech", "offer", "hired", "rejected"];

  const [candidates, setCandidates] = useState([
    { id: "1", name: "Amit", email: "amit@example.com", stage: "tech" },
    { id: "2", name: "Priya", email: "priya@example.com", stage: "offer" },
    { id: "3", name: "Rohan", email: "rohan@example.com", stage: "hired" },
  ]);

  const sensors = useSensors(useSensor(PointerSensor));

  const handleDragEnd = (event) => {
    const { active, over } = event;
    if (!over) return;

    const activeId = active.id;
    const overColumnId = over.data.current?.columnId;

    if (!overColumnId) return;

    // update stage
    setCandidates((prev) =>
      prev.map((c) =>
        c.id === activeId ? { ...c, stage: overColumnId } : c
      )
    );
  };

  return (
    <DndContext
      sensors={sensors}
      onDragEnd={handleDragEnd}
      collisionDetection={closestCenter}
    >
      <div
        style={{
          display: "flex",
          gap: "20px",
          padding: "20px",
          background: "#f5f5f5",
          minHeight: "100vh",
        }}
      >
        {stages.map((stage) => (
          <KanbanColumn
            key={stage}
            id={stage}
            title={stage}
            candidates={candidates.filter((c) => c.stage === stage)}
          />
        ))}
      </div>
    </DndContext>
  );
}
