type IDBKeyNamesVar = typeof IDBKeyRange;

function getDbNamesTable(indexedDB: IDBFactory, IDBKeyRange: IDBKeyNamesVar) {
  let dbNamesDB = indexedDB["_dbNamesDB"];
  if (!dbNamesDB) {
    dbNamesDB = indexedDB["_dbNamesDB"] = new Dexie(DBNAMES_DB, {
      addons: [],
      indexedDB,
