import React from "react";
import { Routes, Route, Link } from "react-router-dom";

// âœ… Import all pages/components
import JobsBoard from "./components/Jobs/JobsBoard";
import JobDetail from "./components/Jobs/JobDetail";
import CandidatesList from "./components/Candidates/CandidatesList";
import CandidateProfile from "./components/Candidates/CandidateProfile";
import Builder from "./components/Assessments/Builder";
import KanbanBoard from "./components/Candidates/KanbanBoard"; // <-- added for drag & drop board

export default function App() {
  return (
    <div className="container" style={{ padding: 20 }}>
      {/* ðŸŒŸ Header Navigation */}
      <header
        className="header"
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: 20,
        }}
      >
        <h1 style={{ margin: 0 }}>TalentFlow</h1>
        <nav style={{ display: "flex", gap: "12px" }}>
          <Link to="/jobs">Jobs</Link>
          <Link to="/candidates">Candidates</Link>
          <Link to="/assessments">Assessments</Link>
        </nav>
      </header>

      {/* ðŸ”— Routes */}
      <Routes>
        {/* Default route */}
        <Route path="/" element={<JobsBoard />} />

        {/* Jobs */}
        <Route path="/jobs" element={<JobsBoard />} />
        <Route path="/jobs/:jobId" element={<JobDetail />} />

        {/* Candidates */}
        {/* Show Kanban drag-drop board on /candidates */}
        <Route
          path="/candidates"
          element={
            <div>
              <h2 className="text-xl font-semibold mb-4">Candidate Pipeline</h2>
              <KanbanBoard />
            </div>
          }
        />
        <Route path="/candidates/:id" element={<CandidateProfile />} />

        {/* Assessments */}
        <Route path="/assessments" element={<Builder />} />
        <Route path="/assessments/:jobId" element={<Builder />} />
      </Routes>
    </div>
  );
}
