import React from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function RuntimeForm() {
  const { assessments } = useAssessmentStore();

  if (!assessments || assessments.length === 0) {
    return (
      <p
        style={{
          padding: "20px",
          fontFamily: "Poppins, sans-serif",
          color: "#666",
        }}
      >
        ‚ö†Ô∏è No assessments available yet.
      </p>
    );
  }

  return (
    <div style={{ padding: 30, fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: 22, fontWeight: 600, marginBottom: 20 }}>
        üìã Live Preview (Fillable Form)
      </h2>

      {assessments.map((a) => (
        <form
          key={a.id}
          style={{
            border: "1px solid #ddd",
            borderRadius: 12,
            padding: 20,
            marginBottom: 25,
            background: "#fff",
          }}
        >
          <h3 style={{ color: "#007bff", marginBottom: 15 }}>{a.title}</h3>

          {a.sections.map((s, si) => (
            <div key={si} style={{ marginBottom: 20 }}>
              <h4 style={{ fontWeight: 600, marginBottom: 10 }}>{s.name}</h4>
              {s.questions.map((q, qi) => (
                <div key={qi} style={{ marginBottom: 12 }}>
                  <label style={{ display: "block", marginBottom: 6 }}>
                    {q.text}
                  </label>

                  {q.type === "short" && (
                    <input
                      type="text"
                      placeholder="Type your answer..."
                      style={{
                        width: "100%",
                        padding: 8,
                        borderRadius: 6,
                        border: "1px solid #ccc",
                      }}
                    />
                  )}

                  {q.type === "long" && (
                    <textarea
                      rows="3"
                      placeholder="Type detailed answer..."
                      style={{
                        width: "100%",
                        padding: 8,
                        borderRadius: 6,
                        border: "1px solid #ccc",
                      }}
                    />
                  )}

                  {q.type === "numeric" && (
                    <input
                      type="number"
                      placeholder="Enter number..."
                      style={{
                        width: "100%",
                        padding: 8,
                        borderRadius: 6,
                        border: "1px solid #ccc",
                      }}
                    />
                  )}

                  {q.type === "single" &&
                    q.options?.map((opt, i) => (
                      <div key={i}>
                        <label>
                          <input
                            type="radio"
                            name={`${a.id}-${s.name}-${qi}`}
                            style={{ marginRight: 6 }}
                          />
                          {opt}
                        </label>
                      </div>
                    ))}

                  {q.type === "multi" &&
                    q.options?.map((opt, i) => (
                      <div key={i}>
                        <label>
                          <input type="checkbox" style={{ marginRight: 6 }} />
                          {opt}
                        </label>
                      </div>
                    ))}
                </div>
              ))}
            </div>
          ))}
        </form>
      ))}
    </div>
  );
}
