import React, { useState } from "react";
import { DndContext, useDraggable, useDroppable } from "@dnd-kit/core";
import CandidateCard from "./CandidateCard";

const stages = ["Applied", "Interview", "Hired"];

const KanbanBoard = () => {
  const [candidates, setCandidates] = useState([
    { id: "1", name: "Viren", stage: "Applied" },
    { id: "2", name: "Amit", stage: "Interview" },
    { id: "3", name: "Sneha", stage: "Hired" },
  ]);

  const handleDragEnd = (event) => {
    const { over, active } = event;
    if (!over) return;
    const newStage = over.id;
    setCandidates((prev) =>
      prev.map((c) =>
        c.id === active.id ? { ...c, stage: newStage } : c
      )
    );
  };

  return (
    <DndContext onDragEnd={handleDragEnd}>
      <div className="flex gap-6 p-6">
        {stages.map((stage) => (
          <StageColumn
            key={stage}
            id={stage}
            title={stage}
            candidates={candidates.filter((c) => c.stage === stage)}
          />
        ))}
      </div>
    </DndContext>
  );
};

const StageColumn = ({ id, title, candidates }) => {
  const { setNodeRef } = useDroppable({ id });

  return (
    <div
      ref={setNodeRef}
      className="bg-gray-100 p-4 rounded-xl w-1/3 min-h-[300px]"
    >
      <h2 className="text-lg font-semibold mb-4">{title}</h2>
      {candidates.map((c) => (
        <DraggableCard key={c.id} candidate={c} />
      ))}
    </div>
  );
};

const DraggableCard = ({ candidate }) => {
  const { attributes, listeners, setNodeRef, transform } = useDraggable({
    id: candidate.id,
  });
  const style = {
    transform: transform
      ? `translate(${transform.x}px, ${transform.y}px)`
      : undefined,
  };

  return (
    <div
      ref={setNodeRef}
      {...listeners}
      {...attributes}
      style={style}
      className="bg-white shadow p-3 mb-3 rounded-lg cursor-move"
    >
      <p className="font-medium">{candidate.name}</p>
      <p className="text-sm text-gray-500">{candidate.stage}</p>
    </div>
  );
};

export default KanbanBoard;
