import React, { useEffect, useState } from "react";
import Dexie from "dexie";

// âœ… Create or open IndexedDB
const db = new Dexie("TalentFlowDB");
db.version(1).stores({
  assessments: "++id,title,sections",
});

// âœ… Seed sample data (only once)
const seedData = async () => {
  const count = await db.assessments.count();
  if (count === 0) {
    const sampleAssessments = [
      {
        title: "Frontend Developer Assessment",
        sections: [
          {
            name: "Technical Skills",
            questions: [
              { type: "short", text: "What is React?" },
              { type: "long", text: "Explain Virtual DOM." },
              { type: "single", text: "Which hook manages state?", options: ["useState", "useMemo", "useRef"] },
              { type: "multi", text: "Select frontend frameworks:", options: ["React", "Angular", "Vue"] },
              { type: "numeric", text: "Years of experience?", min: 0, max: 10 },
              { type: "file", text: "Upload your project zip" },
              { type: "short", text: "Define props." },
              { type: "long", text: "Explain component lifecycle." },
              { type: "single", text: "JSX stands for?", options: ["JavaScript XML", "JSON XML", "Java Syntax"] },
              { type: "multi", text: "Select valid hooks:", options: ["useState", "useEffect", "useContext"] },
            ],
          },
        ],
      },
      {
        title: "Backend Developer Assessment",
        sections: [
          {
            name: "API Design",
            questions: [
              { type: "short", text: "What is an API?" },
              { type: "long", text: "Explain REST vs GraphQL." },
              { type: "single", text: "Node.js is ___?", options: ["Runtime", "Framework", "Language"] },
              { type: "multi", text: "Select databases:", options: ["MySQL", "MongoDB", "Postgres"] },
              { type: "numeric", text: "Rate Node.js skill (1-10)", min: 1, max: 10 },
              { type: "file", text: "Upload backend code" },
              { type: "short", text: "What is middleware?" },
              { type: "long", text: "Explain event loop." },
              { type: "single", text: "Express.js used for?", options: ["APIs", "ML", "Frontend"] },
              { type: "multi", text: "Select async patterns:", options: ["Promises", "Callbacks", "Async/Await"] },
            ],
          },
        ],
      },
      {
        title: "UI/UX Designer Assessment",
        sections: [
          {
            name: "Design Principles",
            questions: [
              { type: "short", text: "What is UI design?" },
              { type: "long", text: "Explain color psychology." },
              { type: "single", text: "Which is a vector tool?", options: ["Figma", "Photoshop", "MS Paint"] },
              { type: "multi", text: "Select UX methods:", options: ["Wireframing", "Prototyping", "Testing"] },
              { type: "numeric", text: "Years of experience?", min: 0, max: 15 },
              { type: "file", text: "Upload portfolio" },
              { type: "short", text: "Define typography." },
              { type: "long", text: "What is responsive design?" },
              { type: "single", text: "Which unit is scalable?", options: ["em", "px", "cm"] },
              { type: "multi", text: "Select design tools:", options: ["Figma", "Canva", "Adobe XD"] },
            ],
          },
        ],
      },
    ];

    await db.assessments.bulkAdd(sampleAssessments);
    console.log("âœ… 3 Assessments seeded successfully!");
  }
};

export default function Builder() {
  const [assessments, setAssessments] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  // âœ… Load + seed data
  useEffect(() => {
    const loadData = async () => {
      await seedData();
      const data = await db.assessments.toArray();
      setAssessments(data || []);
    };
    loadData();
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: "24px", fontWeight: "600", marginBottom: "20px" }}>
        ðŸ§© Assessment Builder
      </h2>

      {assessments.length === 0 ? (
        <p>Loading assessments...</p>
      ) : (
        assessments.map((a) => (
          <div
            key={a.id}
            style={{
              background: "#cad7e3ff",
              marginBottom: "15px",
              padding: "20px",
              borderRadius: "12px",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
            }}
          >
            <h3
              onClick={() => toggleExpand(a.id)}
              style={{
                cursor: "pointer",
                color: "#007bff",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              {a.title}
              <span>{expandedId === a.id ? "â–²" : "â–¼"}</span>
            </h3>

            {expandedId === a.id && (
              <div style={{ marginTop: "12px" }}>
                {(a.sections || []).map((s, i) => (
                  <div key={i} style={{ marginBottom: "12px" }}>
                    <h4 style={{ fontWeight: "600", marginBottom: "5px" }}>
                      {s.name}
                    </h4>
                    <ul style={{ paddingLeft: "20px", lineHeight: "1.8" }}>
                      {(s.questions || []).map((q, idx) => (
                        <li key={idx}>
                          {q.text}{" "}
                          <small style={{ color: "#888" }}>
                            ({q.type}
                            {q.options ? ` â€¢ ${q.options.length} options` : ""})
                          </small>
                        </li>
                      ))}
                    </ul>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
