import React, {useEffect, useState} from 'react';
import db from '../../db/dexie';
import Preview from './Preview';
import { useParams } from 'react-router-dom';


export default function Builder(){
const {jobId} = useParams();
const [job, setJob] = useState(jobId? Number(jobId): null);
const [assessment, setAssessment] = useState({title:'New Assessment', sections:[{title:'Section 1',questions:[]} ]});
const [selectedSection, setSelectedSection] = useState(0);


useEffect(()=>{
async function load(){
if (!job) return;
const res = await fetch(`/assessments/${job}`);
const data = await res.json();
if (data) setAssessment(data);
}
load();
},[job]);
function addQuestion(){
const q = {id: 'q'+Date.now(), type:'short', label:'New question', required:false};
const clone = {...assessment};
clone.sections[selectedSection].questions.push(q);
setAssessment({...clone});
}


async function save(){
if (!job) return alert('select jobId in url like /assessments/1');
await fetch(`/assessments/${job}`,{method:'PUT',headers:{'content-type':'application/json'},body:JSON.stringify(assessment)});
alert('saved');
}


return (
<div style={{display:'flex',gap:12}}>
<div style={{flex:1}}>
<div className="card">
<h3>Builder - {assessment.title}</h3>
<div>
<label>Title</label>
<input value={assessment.title} onChange={e=> setAssessment({...assessment, title: e.target.value})} />
</div>
<div style={{marginTop:8}}>
<button onClick={()=> setAssessment({...assessment, sections: [...assessment.sections, {title:'New Section', questions:[]} ]})}>Add Section</button>
<button onClick={addQuestion}>Add Question</button>
<button onClick={save}>Save Assessment</button>
</div>


<div style={{marginTop:12}}>
{assessment.sections.map((s,idx)=> (
<div key={idx} style={{marginBottom:10}} className="card">
<div style={{display:'flex',justifyContent:'space-between'}}>
<div>
<input value={s.title} onChange={e=>{
const clone = {...assessment}; clone.sections[idx].title = e.target.value; setAssessment({...clone});
}} />
<div className="small">{s.questions.length} questions</div>
</div>
<div>
<button onClick={()=> setSelectedSection(idx)}>Edit</button>
</div>
</div>
<div style={{marginTop:8}}>
{s.questions.map((q,qi)=> (
<div key={q.id} style={{padding:6,border:'1px dashed #eee',marginBottom:6}}>
<input value={q.label} onChange={e=>{
const clone = {...assessment}; clone.sections[idx].questions[qi].label = e.target.value; setAssessment({...clone});
}} />
<select value={q.type} onChange={e=>{
const clone = {...assessment}; clone.sections[idx].questions[qi].type = e.target.value; setAssessment({...clone});
}}>
<option value="short">Short text</option>
<option value="long">Long text</option>
<option value="single">Single choice</option>
<option value="multi">Multi choice</option>
<option value="numeric">Numeric</option>
<option value="file">File upload</option>
</select>
</div>
))}
</div>
</div>
))}
</div>
</div>
</div>


<div style={{width:420}}>
<Preview assessment={assessment} onChange={setAssessment} />
</div>
</div>
)
}
