import React, { useState } from "react";
import { useCandidates } from "../../hooks/useApi";
import CandidateCard from "./CandidateCard";

export default function CandidatesList() {
  const [page, setPage] = useState(1);
  const [search, setSearch] = useState("");
  const [stage, setStage] = useState("");
  const pageSize = 12;

  const { data, isLoading, error } = useCandidates({ search, stage, page, pageSize });

  if (isLoading) return <div className="card">Loading candidates...</div>;
  if (error) return <div className="card">Error loading candidates</div>;

  const items = data?.items || [];

  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
        <div>
          <input
            placeholder="Search candidates"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <select
            value={stage}
            onChange={(e) => setStage(e.target.value)}
            style={{ marginLeft: 8 }}
          >
            <option value="">All stages</option>
            <option value="applied">Applied</option>
            <option value="screen">Screen</option>
            <option value="tech">Tech</option>
            <option value="offer">Offer</option>
            <option value="hired">Hired</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>
      </div>

      <div className="card">
        <div className="candidate-list">
          {items.length > 0 ? (
            items.map((c) => <CandidateCard key={c.id} candidate={c} />)
          ) : (
            <div>No candidates found.</div>
          )}
        </div>
      </div>

      <div style={{ marginTop: 12 }}>
        <button disabled={page <= 1} onClick={() => setPage((p) => p - 1)}>
          Prev
        </button>
        <span style={{ margin: "0 8px" }}>Page {page}</span>
        <button
          disabled={items.length < pageSize}
          onClick={() => setPage((p) => p + 1)}
        >
          Next
        </button>
      </div>
    </div>
  );
}
