// src/components/Assessments/Builder.jsx
import React, { useEffect, useState } from "react";
import { db } from "../../db"; // âœ… use the same db instance

export default function Builder() {
  const [assessments, setAssessments] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  // âœ… Load all assessments and their related questions
  useEffect(() => {
    const loadData = async () => {
      // Get all assessments
      const allAssessments = await db.assessments.toArray();

      // For each assessment, get its questions
      const dataWithQuestions = await Promise.all(
        allAssessments.map(async (a) => {
          const questions = await db.questions
            .where("assessmentId")
            .equals(a.id)
            .toArray();
          return { ...a, questions };
        })
      );

      setAssessments(dataWithQuestions);
      console.log("âœ… Loaded assessments with questions:", dataWithQuestions);
    };

    loadData();
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ§© Assessment Builder</h2>

      {assessments.length === 0 ? (
        <p>Loading assessments...</p>
      ) : (
        assessments.map((a) => (
          <div
            key={a.id}
            style={{
              background: "#f8f9fa",
              margin: "10px 0",
              padding: "15px",
              borderRadius: "8px",
              boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            }}
          >
            <h3
              onClick={() => toggleExpand(a.id)}
              style={{
                cursor: "pointer",
                color: "#007bff",
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              {a.title}
              <span>{expandedId === a.id ? "â–²" : "â–¼"}</span>
            </h3>

            {expandedId === a.id && (
              <div style={{ marginTop: "10px" }}>
                <h4>Questions ({a.questions.length})</h4>
                <ul style={{ paddingLeft: "20px" }}>
                  {a.questions.map((q) => (
                    <li key={q.id}>{q.text}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
