import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./useAssessmentStore";
import RuntimeForm from "./RuntimeForm";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment, addQuestion } =
    useAssessmentStore();
  const [selectedAssessment, setSelectedAssessment] = useState(null);
  const [newTitle, setNewTitle] = useState("");
  const [showQuestionForm, setShowQuestionForm] = useState(false);
  const [questionData, setQuestionData] = useState({
    text: "",
    type: "short",
    options: "",
  });

  useEffect(() => {
    fetchAssessments();
  }, []);

  const handleAddAssessment = () => {
    if (!newTitle.trim()) return;
    addAssessment(newTitle);
    setNewTitle("");
  };

  const handleAddQuestion = () => {
    if (!selectedAssessment) return alert("Select an assessment first!");
    setShowQuestionForm(true);
  };

  const handleSaveQuestion = () => {
    const q = {
      text: questionData.text,
      type: questionData.type,
      options:
        questionData.type === "multi" || questionData.type === "single"
          ? questionData.options.split(",").map((o) => o.trim())
          : [],
    };

    addQuestion(selectedAssessment.id, "Section 1", q);
    setShowQuestionForm(false);
    setQuestionData({ text: "", type: "short", options: "" });
  };

  return (
    <div className="flex flex-col md:flex-row gap-6 p-6 bg-gray-100 min-h-screen">
      {/* üß± Left Panel */}
      <div className="flex-1 bg-white p-6 rounded-2xl shadow-md">
        <h2 className="text-xl font-bold mb-4 text-gray-800">
          üß© Assessment Builder
        </h2>

        {/* ‚ûï Add Assessment */}
        <div className="flex mb-4 gap-2">
          <input
            className="border rounded px-2 py-1 flex-grow"
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="New Assessment Title"
          />
          <button
            className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700"
            onClick={handleAddAssessment}
          >
            Add
          </button>
        </div>

        {/* üìú Assessment List */}
        <ul className="space-y-2">
          {assessments.map((a) => (
            <li key={a.id}>
              <button
                className={`w-full text-left px-3 py-2 rounded transition ${
                  selectedAssessment?.id === a.id
                    ? "bg-blue-100 border border-blue-400"
                    : "bg-gray-100 hover:bg-gray-200"
                }`}
                onClick={() => setSelectedAssessment(a)}
              >
                {a.title}
              </button>
            </li>
          ))}
        </ul>

        {/* Add Question Button */}
        {selectedAssessment && (
          <button
            onClick={handleAddQuestion}
            className="mt-4 bg-green-600 text-white px-4 py-1 rounded hover:bg-green-700"
          >
            ‚ûï Add Question
          </button>
        )}

        {/* Question Form */}
        {showQuestionForm && (
          <div className="mt-6 p-4 border rounded-lg bg-gray-50 space-y-3">
            <h4 className="font-semibold text-gray-700">Add New Question</h4>

            <input
              className="border w-full px-2 py-1 rounded"
              type="text"
              placeholder="Enter question text"
              value={questionData.text}
              onChange={(e) =>
                setQuestionData({ ...questionData, text: e.target.value })
              }
            />

            <select
              className="border w-full px-2 py-1 rounded"
              value={questionData.type}
              onChange={(e) =>
                setQuestionData({ ...questionData, type: e.target.value })
              }
            >
              <option value="single">Single Choice</option>
              <option value="multi">Multi Choice</option>
              <option value="short">Short Text</option>
              <option value="long">Long Text</option>
              <option value="numeric">Numeric (Range)</option>
              <option value="file">File Upload</option>
            </select>

            {(questionData.type === "multi" ||
              questionData.type === "single") && (
              <input
                className="border w-full px-2 py-1 rounded"
                type="text"
                placeholder="Enter options separated by commas"
                value={questionData.options}
                onChange={(e) =>
                  setQuestionData({
                    ...questionData,
                    options: e.target.value,
                  })
                }
              />
            )}

            <div className="flex gap-2">
              <button
                className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700"
                onClick={handleSaveQuestion}
              >
                Save
              </button>
              <button
                className="bg-gray-300 text-gray-800 px-4 py-1 rounded hover:bg-gray-400"
                onClick={() => setShowQuestionForm(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>

      {/* üëÅÔ∏è Live Preview */}
      <div className="flex-1 bg-gray-50 p-6 rounded-2xl shadow-inner">
        <h3 className="text-lg font-bold mb-2 text-gray-700">üìã Live Preview</h3>
        {selectedAssessment ? (
          <RuntimeForm assessment={selectedAssessment} />
        ) : (
          <p className="text-gray-500">
            Select an assessment to preview its questions.
          </p>
        )}
      </div>
    </div>
  );
}
