// src/db/seed.js
import { db } from "./dexie";

export async function seedAssessments() {
  const count = await db.assessments.count();
  if (count > 0) return; // Already seeded

  for (let jobId = 1; jobId <= 3; jobId++) {
    const assessmentId = await db.assessments.add({
      jobId,
      title: `Assessment for Job ${jobId}`,
    });

    const questionTypes = ["short", "long", "single", "multi", "numeric", "file"];

    const questions = Array.from({ length: 10 }, (_, i) => ({
      assessmentId,
      text: `Question ${i + 1} for Job ${jobId}`,
      type: questionTypes[i % questionTypes.length],
      required: Math.random() > 0.4,
    }));

    await db.questions.bulkAdd(questions);
  }

  console.log("✅ Seeded 3 assessments with 10 questions each");
}
