import Dexie from 'dexie';

// 1ï¸âƒ£ Create and define the database
export const db = new Dexie('TalentFlowDB');

db.version(1).stores({
  jobs: '++id, title, slug, status, tags, order',      // jobs list
  candidates: '++id, name, email, stage, jobId',       // candidates per job
  timelines: '++id, candidateId, stage, date',         // candidate movement timeline
  assessments: '++id, jobId, title',                   // assessments per job
  questions: '++id, assessmentId, text, type, required', // assessment questions
  submissions: '++id, jobId, name, answers, submittedAt' // assessment submissions
});

// 2ï¸âƒ£ Utility: Simulate latency
export const latency = (min = 200, max = 1200) =>
  new Promise(resolve => setTimeout(resolve, Math.floor(Math.random() * (max - min + 1)) + min));

// 3ï¸âƒ£ Utility: Simulate write failure (7%)
export const maybeFail = () => Math.random() < 0.07;

// 4ï¸âƒ£ Seed initial data for testing
export async function seedData() {
  // ðŸ§© Jobs
  const jobsCount = await db.jobs.count();
  if (jobsCount === 0) {
    const jobs = Array.from({ length: 25 }, (_, i) => ({
      title: `Job ${i + 1}`,
      slug: `job-${i + 1}`,
      status: Math.random() > 0.3 ? 'active' : 'archived',
      tags: ['frontend', 'backend', 'remote'],
      order: i + 1,
    }));
    await db.jobs.bulkAdd(jobs);
  }

  // ðŸ‘¥ Candidates
  const stages = ['applied', 'screen', 'tech', 'offer', 'hired', 'rejected'];
  const candidatesCount = await db.candidates.count();
  if (candidatesCount === 0) {
    const candidates = Array.from({ length: 1000 }, (_, i) => ({
      name: `Candidate ${i + 1}`,
      email: `candidate${i + 1}@example.com`,
      stage: stages[Math.floor(Math.random() * stages.length)],
      jobId: Math.floor(Math.random() * 25) + 1,
    }));
    await db.candidates.bulkAdd(candidates);
  }

  // ðŸ§  Assessments for first 3 jobs
  const assessmentsCount = await db.assessments.count();
  if (assessmentsCount === 0) {
    for (let jobId = 1; jobId <= 3; jobId++) {
      const assessmentId = await db.assessments.add({
        jobId,
        title: `Assessment for Job ${jobId}`,
      });

      const questions = Array.from({ length: 12 }, (_, q) => ({
        assessmentId,
        text: `Question ${q + 1} for Job ${jobId}`,
        type: 'short-text',
        required: true,
      }));

      await db.questions.bulkAdd(questions);
    }
  }
}
