import React from "react";
import { useAssessmentStore } from "./u";

export default function RuntimeForm() {
  const { assessments } = useAssessmentStore();
  const a = assessments[0]; // just show Assessment 1 for demo

  if (!a) return <p>No assessments found</p>;

  return (
    <form style={{ marginTop: "10px" }}>
      {a.sections[0].questions.map((q) => (
        <div key={q.id} style={{ marginBottom: "10px" }}>
          <label>{q.question}</label>
          <br />
          <input
            type="text"
            placeholder="Your answer..."
            style={{
              width: "100%",
              padding: "5px",
              borderRadius: "5px",
              border: "1px solid #ccc",
            }}
          />
        </div>
      ))}
      <button type="submit" style={{ background: "#007bff", color: "white", padding: "5px 10px", border: "none", borderRadius: "5px" }}>
        Submit
      </button>
    </form>
  );
}
