import React, { useEffect, useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { db } from "../../db/dexie";

const stages = ["applied", "screen", "tech", "offer", "hired", "rejected"];

export default function CandidatesBoard() {
  const [candidates, setCandidates] = useState([]);

  // Load from Dexie
  useEffect(() => {
    db.candidates.toArray().then(setCandidates);
  }, []);

  // Handle drag end
  const handleDragEnd = async (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return; // dropped outside
    if (destination.droppableId === source.droppableId) return; // same column

    // Update stage in Dexie
    await db.candidates.update(Number(draggableId), {
      stage: destination.droppableId,
    });

    // Refresh
    setCandidates(await db.candidates.toArray());
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ textAlign: "center", marginBottom: 20 }}>Kanban Board</h2>

      <DragDropContext onDragEnd={handleDragEnd}>
        <div
          style={{
            display: "flex",
            gap: "16px",
            overflowX: "auto",
            padding: "10px",
          }}
        >
          {stages.map((stage) => (
            <Droppable droppableId={stage} key={stage}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    flex: "0 0 250px",
                    background: "#f9f9f9",
                    borderRadius: 8,
                    padding: 10,
                    border: "1px solid #ddd",
                    minHeight: "500px",
                  }}
                >
                  <h3
                    style={{
                      textAlign: "center",
                      textTransform: "capitalize",
                      color: "#333",
                      marginBottom: 10,
                    }}
                  >
                    {stage}
                  </h3>

                  {candidates
                    .filter((c) => c.stage === stage)
                    .slice(0, 20)
                    .map((c, index) => (
                      <Draggable
                        draggableId={String(c.id)}
                        index={index}
                        key={c.id}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              userSelect: "none",
                              background: "#fff",
                              padding: 10,
                              borderRadius: 6,
                              marginBottom: 10,
                              border: "1px solid #eee",
                              boxShadow:
                                "0 1px 4px rgba(0, 0, 0, 0.08)",
                              ...provided.draggableProps.style,
                            }}
                          >
                            <strong>{c.name}</strong>
                            <div style={{ fontSize: 12, color: "#666" }}>
                              {c.email}
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}

                  {provided.placeholder}

                  {candidates.filter((c) => c.stage === stage).length === 0 && (
                    <div
                      style={{
                        textAlign: "center",
                        color: "#999",
                        fontStyle: "italic",
                        marginTop: 20,
                      }}
                    >
                      No candidates
                    </div>
                  )}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}
