// src/components/Assessments/RuntimeForm.jsx
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useAssessmentStore } from "./";

export default function RuntimeForm() {
  const { assessmentId } = useParams();
  const id = Number(assessmentId);
  const { assessments, init, updateAssessment } = useAssessmentStore();
  const [assessment, setAssessment] = useState(null);
  const [values, setValues] = useState(() => JSON.parse(localStorage.getItem("responses") || "{}"));
  const [errors, setErrors] = useState({});

  useEffect(()=>{ init(); }, [init]);

  useEffect(()=>{
    const a = assessments.find(x => x.id === id);
    setAssessment(a || null);
  }, [assessments, id]);

  function change(qid, val) {
    setValues(prev => ({ ...prev, [qid]: val }));
  }

  function validateAll() {
    const errs = {};
    assessment?.sections?.forEach(s => s.questions?.forEach(q => {
      const show = !q.condition?.dependsOn || values[q.condition.dependsOn] === q.condition.value;
      if (!show) return;
      const v = values[q.id];
      if (q.required && (v === undefined || v === "" || (Array.isArray(v) && v.length===0))) errs[q.id] = "Required";
      if (q.type === "numeric" && v !== undefined && v !== ""){
        const n = Number(v);
        if (q.range?.min != null && n < q.range.min) errs[q.id] = `Min ${q.range.min}`;
        if (q.range?.max != null && n > q.range.max) errs[q.id] = `Max ${q.range.max}`;
      }
      if (q.type === "long" && q.maxLength && v && v.length > q.maxLength) errs[q.id] = `Max ${q.maxLength} chars`;
    }));
    setErrors(errs);
    return Object.keys(errs).length === 0;
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!validateAll()) return alert("Fix errors");
    // store locally (localStorage). You may extend to persist to Dexie or server.
    const key = `responses_${assessment?.id || "unknown"}`;
    const prev = JSON.parse(localStorage.getItem(key) || "[]");
    localStorage.setItem(key, JSON.stringify([...prev, { at: Date.now(), values }]));
    alert("Response saved locally");
    // optionally simulate update assessment metadata
    // await updateAssessment(assessment.id, { lastSavedAt: Date.now() });
  }

  if (!assessment) return <div style={{ padding: 20 }}>Loading assessment...</div>;

  return (
    <div style={{ padding: 20 }}>
      <Link to="/assessments">‚Üê Back</Link>
      <h2>{assessment.title}</h2>

      <form onSubmit={handleSubmit}>
        {assessment.sections?.map((s, si) => (
          <div key={si} style={{ marginBottom: 18 }}>
            <h4>{s.name}</h4>
            {s.questions?.map(q => {
              const show = !q.condition?.dependsOn || values[q.condition?.dependsOn] === q.condition?.value;
              if (!show) return null;
              return (
                <div key={q.id} style={{ marginBottom: 10 }}>
                  <label style={{ display:"block", fontWeight:500 }}>{q.label} {q.required && '*'}</label>

                  {q.type === "short" && <input value={values[q.id]||""} onChange={e=>change(q.id, e.target.value)} style={{ width:"100%", padding:8 }} />}
                  {q.type === "long" && <textarea rows={3} value={values[q.id]||""} onChange={e=>change(q.id, e.target.value)} style={{ width:"100%", padding:8 }} />}
                  {q.type === "numeric" && <input type="number" value={values[q.id]||""} onChange={e=>change(q.id, e.target.value)} style={{ width:"100%", padding:8 }} />}
                  {q.type === "single-choice" && q.options?.map(opt => <div key={opt}><label><input type="radio" name={q.id} checked={values[q.id]===opt} onChange={()=>change(q.id,opt)} /> {opt}</label></div>)}
                  {q.type === "multi-choice" && q.options?.map(opt => {
                    const arr = values[q.id] || [];
                    return <div key={opt}><label><input type="checkbox" checked={arr.includes(opt)} onChange={e=>{
                      const prev = values[q.id] || [];
                      change(q.id, e.target.checked ? [...prev, opt] : prev.filter(x=>x!==opt));
                    }} /> {opt}</label></div>;
                  })}
                  {q.type === "file" && <input type="file" onChange={e=>change(q.id, e.target.files?.[0]?.name || "")} />}

                  {errors[q.id] && <div style={{ color:"red", fontSize:13 }}>{errors[q.id]}</div>}
                </div>
              );
            })}
          </div>
        ))}

        <div>
          <button type="submit" style={{ padding:"8px 12px" }}>Submit</button>
        </div>
      </form>
    </div>
  );
}
