import React, { useState } from "react";
import AssessmentPreview from "./Preview";

export default function Builder() {
  const [assessment, setAssessment] = useState({
    title: "New Assessment",
    sections: [{ id: Date.now(), name: "Section 1", questions: [] }],
  });

  const [selectedSection, setSelectedSection] = useState(null);
  const [sectionName, setSectionName] = useState("");

  const handleAddSection = () => {
    if (!sectionName.trim()) return alert("Enter section name!");
    const newSection = {
      id: Date.now(),
      name: sectionName,
      questions: [],
    };
    setAssessment({
      ...assessment,
      sections: [...assessment.sections, newSection],
    });
    setSectionName("");
  };

  const handleAddQuestion = (type) => {
    if (selectedSection === null) return alert("Select a section first!");
    const question = {
      id: "q" + Date.now(),
      label: `Sample ${type} question`,
      type,
      required: false,
      options: type.includes("choice") ? ["Option 1", "Option 2"] : [],
      condition: null, // for conditional visibility
    };
    const clone = { ...assessment };
    clone.sections[selectedSection].questions.push(question);
    setAssessment(clone);
  };

  const handleTitleChange = (e) => {
    setAssessment({ ...assessment, title: e.target.value });
  };

  const handleQuestionEdit = (secIdx, qIdx, field, value) => {
    const clone = { ...assessment };
    clone.sections[secIdx].questions[qIdx][field] = value;
    setAssessment(clone);
  };

  const saveLocally = () => {
    localStorage.setItem("assessmentData", JSON.stringify(assessment));
    alert("âœ… Assessment saved locally!");
  };

  return (
    <div className="flex gap-6 p-4">
      {/* Left Builder Panel */}
      <div className="w-1/2 border rounded-2xl p-4 shadow-md bg-white">
        <h2 className="text-xl font-semibold mb-2">ðŸ§© Assessment Builder</h2>

        <input
          className="border p-2 rounded w-full mb-3"
          value={assessment.title}
          onChange={handleTitleChange}
          placeholder="Assessment Title"
        />

        <div className="flex gap-2 mb-4">
          <input
            value={sectionName}
            onChange={(e) => setSectionName(e.target.value)}
            placeholder="Section name"
            className="border p-2 rounded w-full"
          />
          <button
            onClick={handleAddSection}
            className="bg-blue-500 text-white px-3 rounded"
          >
            âž• Add Section
          </button>
        </div>

        <div>
          {assessment.sections.map((s, idx) => (
            <div
              key={s.id}
              className={`p-2 border rounded mb-2 cursor-pointer ${
                selectedSection === idx ? "bg-blue-100" : ""
              }`}
              onClick={() => setSelectedSection(idx)}
            >
              <strong>{s.name}</strong>
              <div className="text-sm text-gray-500">
                {s.questions.length} Questions
              </div>
            </div>
          ))}
        </div>

        {selectedSection !== null && (
          <div className="mt-3">
            <h3 className="font-medium mb-2">Add Question</h3>
            {["short", "long", "numeric", "single-choice", "multi-choice", "file"].map(
              (type) => (
                <button
                  key={type}
                  onClick={() => handleAddQuestion(type)}
                  className="border p-1 m-1 rounded hover:bg-gray-100"
                >
                  {type}
                </button>
              )
            )}
          </div>
        )}

        <div className="mt-4">
          <button
            onClick={saveLocally}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            ðŸ’¾ Save Locally
          </button>
        </div>
      </div>

      {/* Right Preview Panel */}
      <div className="w-1/2 border rounded-2xl p-4 shadow-md bg-gray-50">
        <AssessmentPreview assessment={assessment} onChange={setAssessment} />
      </div>
    </div>
  );
}
