import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment, addQuestion } =
    useAssessmentStore();

  const [expanded, setExpanded] = useState(null);
  const [newName, setNewName] = useState("");
  const [questionText, setQuestionText] = useState("");
  const [questionType, setQuestionType] = useState("short");

  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  const toggleExpand = (id) => {
    setExpanded(expanded === id ? null : id);
  };

  const handleAdd = () => {
    if (!newName.trim()) return;
    addAssessment(newName.trim());
    setNewName("");
  };

  const handleAddQuestion = (id) => {
    if (!questionText.trim()) return;
    addQuestion(id, questionText.trim(), questionType);
    setQuestionText("");
  };

  return (
    <div style={{ padding: 30, fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: 22, fontWeight: 600, marginBottom: 20 }}>
        🧩 Assessment Builder
      </h2>

      {assessments.map((a) => (
        <div
          key={a.id}
          onClick={() => toggleExpand(a.id)}
          style={{
            background: "#edf2ff",
            marginBottom: 12,
            padding: 16,
            borderRadius: 10,
            cursor: "pointer",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              fontWeight: 600,
              color: "#007bff",
            }}
          >
            {a.title}
            <span>{expanded === a.id ? "▲" : "▼"}</span>
          </div>

          {expanded === a.id && (
            <div style={{ marginTop: 10, paddingLeft: 10 }}>
              {a.sections.map((s, si) => (
                <div key={si}>
                  <h4 style={{ fontWeight: 600, marginBottom: 6 }}>{s.name}</h4>
                  <ul style={{ marginLeft: 15 }}>
                    {s.questions.map((q, qi) => (
                      <li key={qi}>
                        {q.text}{" "}
                        <small style={{ color: "#777" }}>({q.type})</small>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}

              {/* Add question input */}
              <div style={{ marginTop: 10 }}>
                <input
                  type="text"
                  placeholder="New Question..."
                  value={questionText}
                  onChange={(e) => setQuestionText(e.target.value)}
                  style={{
                    padding: 6,
                    borderRadius: 6,
                    border: "1px solid #ccc",
                    marginRight: 8,
                  }}
                />
                <select
                  value={questionType}
                  onChange={(e) => setQuestionType(e.target.value)}
                  style={{ padding: 6, borderRadius: 6, marginRight: 8 }}
                >
                  <option value="short">Short Answer</option>
                  <option value="long">Long Answer</option>
                  <option value="numeric">Numeric</option>
                  <option value="single">Single Choice</option>
                  <option value="multi">Multiple Choice</option>
                </select>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleAddQuestion(a.id);
                  }}
                  style={{
                    background: "#007bff",
                    color: "#fff",
                    border: "none",
                    padding: "6px 12px",
                    borderRadius: 6,
                    cursor: "pointer",
                  }}
                >
                  ➕ Add Question
                </button>
              </div>
            </div>
          )}
        </div>
      ))}

      {/* Add new assessment */}
      <div style={{ marginTop: 25 }}>
        <input
          type="text"
          placeholder="New Assessment Title"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          style={{
            padding: "8px 12px",
            borderRadius: 6,
            border: "1px solid #ccc",
            marginRight: 8,
          }}
        />
        <button
          onClick={handleAdd}
          style={{
            background: "#007bff",
            color: "#fff",
            border: "none",
            padding: "8px 14px",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          ➕ Add Assessment
        </button>
      </div>
    </div>
  );
}
