import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const initialData = {
  applied: [
    { id: "1", name: "Viren" },
    { id: "2", name: "Riya" },
  ],
  interview: [{ id: "3", name: "Raj" }],
  offer: [],
  hired: [],
  rejected: [],
};

const KanbanBoard = () => {
  const [candidates, setCandidates] = useState(initialData);

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    if (source.droppableId === destination.droppableId) {
      const items = Array.from(candidates[source.droppableId]);
      const [moved] = items.splice(source.index, 1);
      items.splice(destination.index, 0, moved);

      setCandidates({
        ...candidates,
        [source.droppableId]: items,
      });
    } else {
      const sourceItems = Array.from(candidates[source.droppableId]);
      const destItems = Array.from(candidates[destination.droppableId]);
      const [moved] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, moved);

      setCandidates({
        ...candidates,
        [source.droppableId]: sourceItems,
        [destination.droppableId]: destItems,
      });
    }
  };

  const columns = ["applied", "interview", "offer", "hired", "rejected"];

  return (
    <div className="flex gap-4 p-6 bg-gray-100 min-h-screen">
      <DragDropContext onDragEnd={onDragEnd}>
        {columns.map((col) => (
          <Droppable key={col} droppableId={col}>
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className="bg-white shadow-md rounded-xl w-1/5 p-3"
              >
                <h2 className="text-lg font-semibold text-center capitalize mb-2">
                  {col}
                </h2>

                {candidates[col].map((item, index) => (
                  <Draggable
                    key={item.id}
                    draggableId={item.id}
                    index={index}
                  >
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-blue-100 p-3 mb-2 rounded-lg cursor-pointer"
                      >
                        {item.name}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
};

export default KanbanBoard;
