import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import {db} from '../../db/dexie';


export default function CandidateProfile(){
const {id} = useParams();
const [candidate,setCandidate] = useState(null);
const [timeline,setTimeline] = useState([]);


useEffect(()=>{
db.candidates.get(Number(id)).then(setCandidate);
fetch(`/candidates/${id}/timeline`).then(r=>r.json()).then(setTimeline);
},[id]);


if (!candidate) return <div className="card">Loading...</div>


return (
<div>
<div className="card">
<h2>{candidate.name}</h2>
<div className="small">{candidate.email} • {candidate.stage}</div>
</div>
<div style={{marginTop:12}} className="card">
<h3>Timeline</h3>
<ul>
{timeline.map(t=> <li key={t.id}>{new Date(t.at).toLocaleString()} – {t.from} → {t.to} {t.note? '- '+t.note:''}</li>)}
</ul>
</div>
</div>
)
}
