import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Preview from "./Preview";

export default function Builder() {
  const { jobId } = useParams();
  const [job, setJob] = useState(jobId ? Number(jobId) : null);
  const [selectedSection, setSelectedSection] = useState(0);
  const [assessment, setAssessment] = useState({
    title: "New Assessment",
    sections: [{ title: "Section 1", questions: [] }],
  });

  useEffect(() => {
    async function load() {
      if (!job) return;
      try {
        const res = await fetch(`/assessments/${job}`);
        const data = await res.json();
        if (data) setAssessment(data);
      } catch {
        console.log("No saved data found, using default.");
      }
    }
    load();
  }, [job]);

  function addSection() {
    setAssessment({
      ...assessment,
      sections: [...assessment.sections, { title: "New Section", questions: [] }],
    });
  }

  function addQuestion() {
    const q = {
      id: "q" + Date.now(),
      type: "short",
      label: "New question",
      required: false,
      options: [],
      range: { min: 0, max: 100 },
    };
    const clone = { ...assessment };
    clone.sections[selectedSection].questions.push(q);
    setAssessment({ ...clone });
  }

  async function save() {
    if (!job) return alert("Please open URL like /assessments/1");
    await fetch(`/assessments/${job}`, {
      method: "PUT",
      headers: { "content-type": "application/json" },
      body: JSON.stringify(assessment),
    });
    alert("âœ… Assessment Saved Successfully!");
  }

  return (
    <div className="flex flex-col lg:flex-row gap-6 p-6 bg-gray-50 min-h-screen">
      {/* Left Builder */}
      <div className="flex-1 bg-white shadow-lg rounded-2xl p-5">
        <h2 className="text-2xl font-semibold text-blue-700 mb-4">
          ðŸ§© Assessment Builder â€” {assessment.title}
        </h2>

        <div className="space-y-3">
          <div>
            <label className="text-sm font-semibold">Assessment Title</label>
            <input
              value={assessment.title}
              onChange={(e) => setAssessment({ ...assessment, title: e.target.value })}
              className="mt-1 border border-gray-300 rounded-lg p-2 w-full"
            />
          </div>

          <div className="flex flex-wrap gap-2">
            <button
              onClick={addSection}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
            >
              + Add Section
            </button>
            <button
              onClick={addQuestion}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg"
            >
              + Add Question
            </button>
            <button
              onClick={save}
              className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg"
            >
              ðŸ’¾ Save
            </button>
          </div>
        </div>

        {assessment.sections.map((s, idx) => (
          <div
            key={idx}
            className={`mt-5 border rounded-xl p-4 ${
              selectedSection === idx ? "border-blue-500" : "border-gray-200"
            }`}
          >
            <div className="flex justify-between items-center">
              <input
                value={s.title}
                onChange={(e) => {
                  const clone = { ...assessment };
                  clone.sections[idx].title = e.target.value;
                  setAssessment(clone);
                }}
                className="text-lg font-medium border-b border-gray-300 focus:border-blue-500 outline-none"
              />
              <button
                className={`px-3 py-1 rounded-lg ${
                  selectedSection === idx
                    ? "bg-blue-600 text-white"
                    : "bg-gray-100 hover:bg-gray-200"
                }`}
                onClick={() => setSelectedSection(idx)}
              >
                Edit
              </button>
            </div>

            <div className="mt-3">
              {s.questions.length === 0 ? (
                <p className="text-gray-400 text-sm italic">
                  No questions added yet.
                </p>
              ) : (
                s.questions.map((q, qi) => (
                  <div
                    key={q.id}
                    className="border rounded-lg p-3 mb-2 bg-gray-50 hover:bg-gray-100 transition"
                  >
                    <input
                      value={q.label}
                      onChange={(e) => {
                        const clone = { ...assessment };
                        clone.sections[idx].questions[qi].label = e.target.value;
                        setAssessment(clone);
                      }}
                      placeholder="Question text..."
                      className="border-b border-gray-300 w-full mb-2 p-1"
                    />

                    <div className="flex justify-between items-center">
                      <select
                        value={q.type}
                        onChange={(e) => {
                          const clone = { ...assessment };
                          clone.sections[idx].questions[qi].type = e.target.value;
                          setAssessment(clone);
                        }}
                        className="border rounded p-1 text-sm"
                      >
                        <option value="short">Short Text</option>
                        <option value="long">Long Text</option>
                        <option value="single">Single Choice</option>
                        <option value="multi">Multi Choice</option>
                        <option value="numeric">Numeric (with range)</option>
                        <option value="file">File Upload</option>
                      </select>

                      <label className="text-sm">
                        <input
                          type="checkbox"
                          checked={q.required}
                          onChange={(e) => {
                            const clone = { ...assessment };
                            clone.sections[idx].questions[qi].required =
                              e.target.checked;
                            setAssessment(clone);
                          }}
                          className="mr-1"
                        />
                        Required
                      </label>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Right Preview */}
      <div className="lg:w-[420px] bg-white shadow-lg rounded-2xl p-4">
        <h3 className="text-lg font-semibold text-green-700 mb-3">ðŸ‘€ Live Preview</h3>
        <Preview assessment={assessment} onChange={setAssessment} />
      </div>
    </div>
  );
}
