import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "@/store/useAssessmentStore";

export default function AssessmentPreview() {
  const { assessments, fetchAssessments } = useAssessmentStore();
  const [expandedId, setExpandedId] = useState(null);
  const [answers, setAnswers] = useState(() => {
    const saved = localStorage.getItem("assessmentAnswers");
    return saved ? JSON.parse(saved) : {};
  });

  // ðŸ”¹ Fetch from IndexedDB (Dexie)
  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  // ðŸ”¹ Save answers in localStorage
  useEffect(() => {
    localStorage.setItem("assessmentAnswers", JSON.stringify(answers));
  }, [answers]);

  // ðŸ”¹ Handle expand/collapse
  const toggleExpand = (id) => {
    setExpandedId((prev) => (prev === id ? null : id));
  };

  const handleChange = (qid, value) => {
    setAnswers((prev) => ({ ...prev, [qid]: value }));
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">ðŸ§© Assessment Preview</h2>

      {assessments.length === 0 ? (
        <p>Loading assessments...</p>
      ) : (
        assessments.map((a) => (
          <div
            key={a.id}
            className="bg-gray-50 border rounded-2xl mb-3 shadow-sm"
          >
            {/* ðŸ”¹ Clickable header */}
            <div
              onClick={() => toggleExpand(a.id)}
              style={{
                cursor: "pointer",
                color: "#007bff",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "12px 16px",
              }}
            >
              <h3 className="text-lg font-semibold">{a.title}</h3>
              <span style={{ fontSize: "18px" }}>
                {expandedId === a.id ? "â–²" : "â–¼"}
              </span>
            </div>

            {/* ðŸ”¹ Expanded content */}
            {expandedId === a.id && (
              <div className="p-4 border-t bg-white rounded-b-2xl">
                {a.sections?.map((s, si) => (
                  <div key={si} className="mt-3">
                    <h4 className="font-semibold mb-2 text-gray-700">{s.name}</h4>

                    {(s.questions || []).map((q, qi) => (
                      <div key={qi} className="mb-4">
                        <label className="block font-medium mb-1">{q.text}</label>

                        {/* Short Answer */}
                        {q.type === "short" && (
                          <input
                            type="text"
                            value={answers[q.text] || ""}
                            onChange={(e) => handleChange(q.text, e.target.value)}
                            className="border p-2 rounded w-full"
                          />
                        )}

                        {/* Long Answer */}
                        {q.type === "long" && (
                          <textarea
                            rows={3}
                            value={answers[q.text] || ""}
                            onChange={(e) => handleChange(q.text, e.target.value)}
                            className="border p-2 rounded w-full"
                          ></textarea>
                        )}

                        {/* Numeric */}
                        {q.type === "numeric" && (
                          <input
                            type="number"
                            value={answers[q.text] || ""}
                            onChange={(e) => handleChange(q.text, e.target.value)}
                            className="border p-2 rounded w-full"
                          />
                        )}

                        {/* Single Choice */}
                        {q.type === "single" &&
                          q.options?.map((opt, oi) => (
                            <div key={oi}>
                              <input
                                type="radio"
                                name={q.text}
                                checked={answers[q.text] === opt}
                                onChange={() => handleChange(q.text, opt)}
                              />{" "}
                              {opt}
                            </div>
                          ))}

                        {/* Multi Choice */}
                        {q.type === "multi" &&
                          q.options?.map((opt, oi) => (
                            <div key={oi}>
                              <input
                                type="checkbox"
                                checked={answers[q.text]?.includes(opt)}
                                onChange={(e) => {
                                  const prev = answers[q.text] || [];
                                  handleChange(
                                    q.text,
                                    e.target.checked
                                      ? [...prev, opt]
                                      : prev.filter((x) => x !== opt)
                                  );
                                }}
                              />{" "}
                              {opt}
                            </div>
                          ))}

                        {/* File Upload */}
                        {q.type === "file" && (
                          <input
                            type="file"
                            onChange={(e) =>
                              handleChange(
                                q.text,
                                e.target.files?.[0]?.name || ""
                              )
                            }
                            className="border p-2 rounded w-full"
                          />
                        )}
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
