// src/components/Assessments/Builder.jsx
import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "../../store/useAssessmentStore";
import Preview from "./Preview";

export default function Builder() {
  const { assessments, init, createAssessment, updateAssessment, loading, error } = useAssessmentStore();
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(null); // local editable copy for selected
  const [newTitle, setNewTitle] = useState("");

  useEffect(() => { init(); }, [init]);

  useEffect(() => {
    if (selected) {
      const found = assessments.find(a => a.id === selected);
      setEditing(found ? JSON.parse(JSON.stringify(found)) : null);
    } else {
      setEditing(null);
    }
  }, [selected, assessments]);

  function emptyQuestion(type = "short") {
    return {
      id: "q" + Date.now(),
      label: "New question",
      type,
      required: false,
      maxLength: type === "long" ? 300 : undefined,
      range: type === "numeric" ? { min: 0, max: 100 } : undefined,
      options: (type === "single-choice" || type === "multi-choice") ? ["Option 1","Option 2"] : undefined,
      condition: { dependsOn: "", value: "" }
    };
  }

  const handleAddAssessment = async () => {
    if (!newTitle.trim()) return alert("Enter title");
    const a = {
      title: newTitle.trim(),
      sections: [{ id: 1, name: "Section 1", questions: Array.from({length:12}, (_,i)=>({ ...emptyQuestion((i%5===0?'short':i%5===1?'long':i%5===2?'numeric':i%5===3?'single-choice':'multi-choice')), label:`Question ${i+1}` })) }]
    };
    try {
      await createAssessment(a);
      setNewTitle("");
    } catch (err) {
      alert(err.message || "Create failed (simulated)");
    }
  };

  const handleSaveEdit = async () => {
    if (!editing) return;
    try {
      await updateAssessment(editing.id, editing);
      alert("Saved");
    } catch (err) {
      alert(err.message || "Save failed (simulated)");
    }
  };

  const addQuestionToEditing = (type) => {
    if (!editing) return;
    editing.sections[0].questions.push(emptyQuestion(type));
    setEditing({ ...editing });
  };

  return (
    <div style={{ display: "flex", gap: 16, padding: 20, fontFamily: "Poppins, sans-serif" }}>
      {/* left - list + builder */}
      <div style={{ flex: 1, minWidth: 360 }}>
        <h2>ðŸ§© Assessment Builder</h2>

        <div style={{ marginBottom: 12 }}>
          <input value={newTitle} onChange={e=>setNewTitle(e.target.value)} placeholder="New assessment title" style={{ padding:8, width: "70%" }} />
          <button onClick={handleAddAssessment} style={{ marginLeft:8, padding: "8px 12px" }}>âž• Add</button>
        </div>

        <div>
          {loading && <div>Loading...</div>}
          {error && <div style={{ color: "red" }}>{error}</div>}

          {assessments.map(a => (
            <div key={a.id} style={{ padding:12, marginBottom:10, borderRadius:8, background: selected===a.id ? "#eaf2ff":"#f5f6f8", cursor:"pointer" }} onClick={()=>setSelected(a.id)}>
              <div style={{ display:"flex", justifyContent:"space-between", alignItems:"center" }}>
                <strong>{a.title}</strong>
                <span style={{ fontSize:14 }}>{selected===a.id ? "â–²":"â–¼"}</span>
              </div>

              {selected===a.id && editing && (
                <div style={{ marginTop:10 }}>
                  <div style={{ marginBottom:8 }}>
                    <label>Title</label>
                    <input value={editing.title} onChange={e=>setEditing({...editing, title: e.target.value})} style={{ width:"100%", padding:8 }} />
                  </div>

                  <div style={{ marginBottom:8 }}>
                    <label>Section name</label>
                    <input value={editing.sections[0].name} onChange={e=>{ editing.sections[0].name = e.target.value; setEditing({...editing}); }} style={{ width:"100%", padding:8 }} />
                  </div>

                  <div style={{ marginTop:8, marginBottom:8 }}>
                    <button onClick={()=>addQuestionToEditing("short")} style={{ marginRight:6 }}>+ Short</button>
                    <button onClick={()=>addQuestionToEditing("long")} style={{ marginRight:6 }}>+ Long</button>
                    <button onClick={()=>addQuestionToEditing("numeric")} style={{ marginRight:6 }}>+ Numeric</button>
                    <button onClick={()=>addQuestionToEditing("single-choice")} style={{ marginRight:6 }}>+ Single</button>
                    <button onClick={()=>addQuestionToEditing("multi-choice")} style={{ marginRight:6 }}>+ Multi</button>
                    <button onClick={()=>addQuestionToEditing("file")} style={{ marginRight:6 }}>+ File</button>
                  </div>

                  <div>
                    <h4 style={{ margin: "8px 0" }}>Questions</h4>
                    {editing.sections[0].questions.map((q, qi) => (
                      <div key={q.id} style={{ border:"1px solid #eee", padding:8, marginBottom:6, borderRadius:6 }}>
                        <input value={q.label} onChange={e=>{ q.label = e.target.value; setEditing({...editing}); }} style={{ width:"100%", marginBottom:6 }} />
                        <div style={{ display:"flex", gap:8, flexWrap:"wrap", alignItems:"center" }}>
                          <select value={q.type} onChange={e=>{ q.type = e.target.value; setEditing({...editing}); }}>
                            <option value="short">Short</option>
                            <option value="long">Long</option>
                            <option value="numeric">Numeric</option>
                            <option value="single-choice">Single-choice</option>
                            <option value="multi-choice">Multi-choice</option>
                            <option value="file">File</option>
                          </select>

                          <label>
                            <input type="checkbox" checked={!!q.required} onChange={e=>{ q.required = e.target.checked; setEditing({...editing}); }} /> Required
                          </label>

                          {q.type === "numeric" && (
                            <div>
                              <input type="number" value={q.range?.min ?? 0} onChange={e=>{ q.range = { ...(q.range||{}), min: Number(e.target.value) }; setEditing({...editing}); }} style={{ width:80 }} /> -
                              <input type="number" value={q.range?.max ?? 100} onChange={e=>{ q.range = { ...(q.range||{}), max: Number(e.target.value) }; setEditing({...editing}); }} style={{ width:80, marginLeft:6 }} />
                            </div>
                          )}

                          {(q.type === "single-choice" || q.type === "multi-choice") && (
                            <div>
                              <small>Options (comma separated)</small>
                              <input value={(q.options||[]).join(",")} onChange={e=>{ q.options = e.target.value.split(",").map(s=>s.trim()); setEditing({...editing}); }} style={{ width:200 }} />
                            </div>
                          )}

                          {q.type === "long" && (
                            <div>
                              <small>Max length</small>
                              <input type="number" value={q.maxLength||300} onChange={e=>{ q.maxLength = Number(e.target.value); setEditing({...editing}); }} style={{ width:100 }} />
                            </div>
                          )}

                          <div>
                            <small>Condition (show if)</small>
                            <div style={{ display:"flex", gap:6, alignItems:"center" }}>
                              <input placeholder="question id" value={q.condition?.dependsOn || ""} onChange={e=>{ q.condition = { ...(q.condition||{}), dependsOn: e.target.value }; setEditing({...editing}); }} style={{ width:120 }} />
                              <input placeholder="value" value={q.condition?.value || ""} onChange={e=>{ q.condition = { ...(q.condition||{}), value: e.target.value }; setEditing({...editing}); }} style={{ width:120 }} />
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  <div style={{ marginTop:8 }}>
                    <button onClick={handleSaveEdit} style={{ padding:"8px 12px", marginRight:8 }}>Save</button>
                    <button onClick={()=>{ setSelected(null); setEditing(null); }} style={{ padding:"8px 12px" }}>Close</button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* right - preview panel */}
      <div style={{ width: 420 }}>
        <Preview assessments={assessments} onOpenRuntime={(id) => window.location.href = `/assessments/runtime/${id}`} />
      </div>
    </div>
  );
}
