import React, { useState } from "react";

export default function Builder() {
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState("");
  const [questionType, setQuestionType] = useState("single-choice");

  const addQuestion = () => {
    if (newQuestion.trim() === "") return;
    setQuestions([
      ...questions,
      { text: newQuestion, type: questionType, options: [] },
    ]);
    setNewQuestion("");
  };

  return (
    <div className="flex flex-col md:flex-row gap-6 p-8 bg-gradient-to-br from-blue-50 via-gray-100 to-blue-100 min-h-screen transition-all duration-300">
      {/* Question Builder Section */}
      <div className="bg-white flex-1 rounded-2xl shadow-xl p-8 border border-gray-200 hover:shadow-2xl transition-all duration-300">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">
          üõ†Ô∏è Build Your Assessment
        </h2>

        <input
          type="text"
          placeholder="Enter your question..."
          value={newQuestion}
          onChange={(e) => setNewQuestion(e.target.value)}
          className="w-full p-3 mb-4 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
        />

        <select
          value={questionType}
          onChange={(e) => setQuestionType(e.target.value)}
          className="w-full p-3 mb-4 border rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
        >
          <option value="single-choice">Single Choice</option>
          <option value="multi-choice">Multiple Choice</option>
          <option value="short-text">Short Text</option>
          <option value="long-text">Long Text</option>
          <option value="numeric">Numeric (Range)</option>
          <option value="file-upload">File Upload</option>
        </select>

        <button
          onClick={addQuestion}
          className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-xl shadow-md transition duration-200"
        >
          ‚ûï Add Question
        </button>
      </div>

      {/* Live Preview Section */}
      <div className="bg-white flex-1 rounded-2xl shadow-xl p-8 border border-gray-200 hover:shadow-2xl transition-all duration-300">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">
          üëÅÔ∏è Live Preview
        </h2>

        {questions.length === 0 ? (
          <p className="text-gray-500 text-center">
            No questions added yet. Start building your assessment!
          </p>
        ) : (
          <div className="space-y-4">
            {questions.map((q, index) => (
              <div
                key={index}
                className="border border-gray-200 p-4 rounded-xl shadow-sm bg-gray-50 hover:bg-gray-100 transition duration-200"
              >
                <p className="font-semibold text-gray-800 mb-2">
                  {index + 1}. {q.text}
                </p>
                <p className="text-sm text-gray-600 italic">
                  Type: {q.type.replace("-", " ")}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
