import React, { useState, useEffect } from "react";
import { useJobs } from "../../hooks/useApi"; // your data hook
import { v4 as uuidv4 } from "uuid";

const JobsBoard = () => {
  const { data: jobsData, isLoading } = useJobs();
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    if (jobsData) setJobs(jobsData);
  }, [jobsData]);

  if (isLoading) return <p className="p-4">Loading jobs...</p>;

  // ‚ûï Add new job instantly
  const handleAddJob = () => {
    const newJob = {
      id: uuidv4(),
      title: "Software Engineer",
      department: "Engineering",
      location: "Nagpur",
      status: "Open",
    };
    setJobs([...jobs, newJob]);
  };

  // ‚úèÔ∏è Edit existing job instantly
  const handleEditJob = (id) => {
    const updatedJobs = jobs.map((job) =>
      job.id === id
        ? { ...job, title: "Updated Job Title", location: "Pune" }
        : job
    );
    setJobs(updatedJobs);
  };

  // üóëÔ∏è Delete job instantly
  const handleDeleteJob = (id) => {
    const updatedJobs = jobs.filter((job) => job.id !== id);
    setJobs(updatedJobs);
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-blue-800">Job Board</h1>

      <button
        onClick={handleAddJob}
        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg shadow mb-5"
      >
        ‚ûï Add New Job
      </button>

      <div className="grid gap-4">
        {jobs.length === 0 ? (
          <p className="text-gray-600">No jobs found. Click ‚ÄúAdd New Job‚Äù.</p>
        ) : (
          jobs.map((job) => (
            <div
              key={job.id}
              className="border rounded-lg p-4 shadow hover:shadow-md transition bg-white"
            >
              <h2 className="text-xl font-semibold text-gray-800">
                {job.title}
              </h2>
              <p className="text-gray-600">{job.department}</p>
              <p className="text-gray-600">{job.location}</p>
              <span
                className={`px-2 py-1 text-sm rounded ${
                  job.status === "Open"
                    ? "bg-green-100 text-green-800"
                    : "bg-gray-100 text-gray-700"
                }`}
              >
                {job.status}
              </span>

              <div className="mt-3 space-x-2">
                <button
                  onClick={() => handleEditJob(job.id)}
                  className="bg-yellow-400 hover:bg-yellow-500 text-white px-3 py-1 rounded"
                >
                  ‚úèÔ∏è Edit
                </button>
                <button
                  onClick={() => handleDeleteJob(job.id)}
                  className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default JobsBoard;
