import React, { useState } from "react";
import { useAssessmentStore } from "./useAssessmentStore";
import RuntimeForm from "./RuntimeForm";

const Builder = () => {
  const { assessments, addAssessment } = useAssessmentStore();
  const [selectedId, setSelectedId] = useState(null);
  const [newTitle, setNewTitle] = useState("");

  const handleAdd = () => {
    if (newTitle.trim()) {
      addAssessment(newTitle);
      setNewTitle("");
    }
  };

  const selectedAssessment = assessments.find((a) => a.id === selectedId);

  return (
    <div className="p-6 flex flex-col gap-6">
      <h1 className="text-2xl font-bold">ðŸ§© Assessment Builder</h1>

      <div className="flex gap-3">
        <input
          type="text"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
          placeholder="New Assessment Name"
          className="border p-2 rounded"
        />
        <button
          onClick={handleAdd}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          + Add
        </button>
      </div>

      <div className="flex gap-10">
        <div className="w-1/3 border-r">
          {assessments.map((a) => (
            <div
              key={a.id}
              className={`cursor-pointer p-2 rounded ${
                selectedId === a.id ? "bg-blue-100" : "hover:bg-gray-100"
              }`}
              onClick={() => setSelectedId(a.id)}
            >
              {a.title}
            </div>
          ))}
        </div>

        <div className="w-2/3">
          {selectedAssessment ? (
            <RuntimeForm assessment={selectedAssessment} />
          ) : (
            <p className="text-gray-500">Select an assessment to preview</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Builder;
