import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment } = useAssessmentStore();
  const [selectedId, setSelectedId] = useState(null);
  const [newTitle, setNewTitle] = useState("");

  // âœ… Load assessments once
  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  const handleAdd = () => {
    if (!newTitle.trim()) return;
    addAssessment(newTitle.trim());
    setNewTitle("");
  };

  const selectedAssessment = assessments.find((a) => a.id === selectedId);

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: 22, fontWeight: 600, marginBottom: 20 }}>
        ðŸ§© Assessment Builder
      </h2>

      {/* âž• Add New Assessment */}
      <div style={{ marginBottom: 25 }}>
        <input
          type="text"
          placeholder="New Assessment Title"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
          style={{
            padding: "8px 12px",
            borderRadius: 6,
            border: "1px solid #ccc",
            marginRight: 8,
          }}
        />
        <button
          onClick={handleAdd}
          style={{
            background: "#007bff",
            color: "#fff",
            border: "none",
            padding: "8px 14px",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          âž• Add
        </button>
      </div>

      <div style={{ display: "flex", gap: 30 }}>
        {/* ðŸ“‹ Assessment List */}
        <div
          style={{
            width: "35%",
            borderRight: "1px solid #ddd",
            paddingRight: 20,
          }}
        >
          <h3 style={{ fontSize: 18, fontWeight: 600, marginBottom: 10 }}>
            All Assessments
          </h3>
          {assessments.map((a) => (
            <div
              key={a.id}
              onClick={() => setSelectedId(a.id)}
              style={{
                padding: "10px 12px",
                borderRadius: 8,
                marginBottom: 8,
                cursor: "pointer",
                background:
                  selectedId === a.id ? "#dbeafe" : "rgba(0,0,0,0.03)",
                color: selectedId === a.id ? "#007bff" : "#333",
                fontWeight: 500,
              }}
            >
              {a.title}
            </div>
          ))}
        </div>

        {/* ðŸ§  Live Preview */}
        <div style={{ width: "65%" }}>
          <h3 style={{ fontSize: 18, fontWeight: 600, marginBottom: 10 }}>
            Live Preview
          </h3>

          {!selectedAssessment ? (
            <p style={{ color: "#777" }}>Select an assessment to preview</p>
          ) : (
            <div
              style={{
                border: "1px solid #ddd",
                borderRadius: 10,
                padding: 16,
                background: "#fff",
              }}
            >
              <h4 style={{ color: "#007bff", marginBottom: 10 }}>
                {selectedAssessment.title}
              </h4>
              {selectedAssessment.sections.map((s, si) => (
                <div key={si}>
                  <h5 style={{ marginBottom: 8 }}>{s.name}</h5>
                  {s.questions.map((q, qi) => (
                    <div key={qi} style={{ marginBottom: 10 }}>
                      <label>{q.text}</label>
                      <div style={{ marginTop: 4 }}>
                        {q.type === "short" && <input type="text" />}
                        {q.type === "long" && <textarea rows="2" />}
                        {q.type === "numeric" && <input type="number" />}
                        {q.type === "single" &&
                          (q.options || ["Option A", "Option B", "Option C"]).map(
                            (opt, oi) => (
                              <div key={oi}>
                                <input type="radio" name={q.text} /> {opt}
                              </div>
                            )
                          )}
                        {q.type === "multi" &&
                          (q.options || ["Option A", "Option B", "Option C"]).map(
                            (opt, oi) => (
                              <div key={oi}>
                                <input type="checkbox" /> {opt}
                              </div>
                            )
                          )}
                      </div>
                    </div>
                  ))}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
