import { create } from "zustand";

export const useAssessmentStore = create((set) => ({
  assessments: [
    {
      id: 1,
      title: "Assessment 1 - Frontend",
      sections: [
        {
          id: 1,
          name: "Frontend Section",
          questions: [
            { id: 1, type: "short", text: "What is React?" },
            { id: 2, type: "long", text: "Explain the Virtual DOM." },
            { id: 3, type: "numeric", text: "What is the default port of React app?" },
            { id: 4, type: "single", text: "Which hook is used for side effects?" },
            { id: 5, type: "multi", text: "Select React hooks." },
            { id: 6, type: "short", text: "What is JSX?" },
            { id: 7, type: "long", text: "Explain React component lifecycle." },
            { id: 8, type: "numeric", text: "How many elements can a React component return?" },
            { id: 9, type: "single", text: "What is useState used for?" },
            { id: 10, type: "multi", text: "Select valid React components." },
            { id: 11, type: "short", text: "What is key prop used for?" },
            { id: 12, type: "long", text: "Explain the difference between class and functional components." },
          ],
        },
      ],
    },
    {
      id: 2,
      title: "Assessment 2 - Backend",
      sections: [
        {
          id: 1,
          name: "Backend Section",
          questions: [
            { id: 1, type: "short", text: "What is Node.js?" },
            { id: 2, type: "long", text: "Explain Express middleware." },
            { id: 3, type: "numeric", text: "Default port number of Express.js?" },
            { id: 4, type: "single", text: "Which module is used for file handling?" },
            { id: 5, type: "multi", text: "Select core Node modules." },
            { id: 6, type: "short", text: "What is NPM?" },
            { id: 7, type: "long", text: "Explain event-driven architecture in Node.js." },
            { id: 8, type: "numeric", text: "How many threads does Node use?" },
            { id: 9, type: "single", text: "Which keyword is used to import modules?" },
            { id: 10, type: "multi", text: "Select backend frameworks." },
            { id: 11, type: "short", text: "What is callback function?" },
            { id: 12, type: "long", text: "Explain the use of async/await." },
          ],
        },
      ],
    },
    {
      id: 3,
      title: "Assessment 3 - Database",
      sections: [
        {
          id: 1,
          name: "Database Section",
          questions: [
            { id: 1, type: "short", text: "What is a primary key?" },
            { id: 2, type: "long", text: "Explain normalization." },
            { id: 3, type: "numeric", text: "Default port number of MySQL?" },
            { id: 4, type: "single", text: "Which SQL command is used to fetch data?" },
            { id: 5, type: "multi", text: "Select SQL constraints." },
            { id: 6, type: "short", text: "What is foreign key?" },
            { id: 7, type: "long", text: "Explain ACID properties." },
            { id: 8, type: "numeric", text: "What is max number of joins in SQL?" },
            { id: 9, type: "single", text: "Which keyword sorts data?" },
            { id: 10, type: "multi", text: "Select database types." },
            { id: 11, type: "short", text: "What is indexing?" },
            { id: 12, type: "long", text: "Explain difference between SQL and NoSQL." },
          ],
        },
      ],
    },
  ],

  addAssessment: (title) =>
    set((state) => ({
      assessments: [
        ...state.assessments,
        { id: Date.now(), title, sections: [] },
      ],
    })),
}));
