import React from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function RuntimeForm() {
  const { assessments } = useAssessmentStore();

  // âœ… If data not loaded yet
  if (!assessments || assessments.length === 0) {
    return <p style={{ padding: "20px" }}>No assessments available yet.</p>;
  }

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: "22px", fontWeight: "600", marginBottom: "20px" }}>
        ðŸ“‹ Live Preview
      </h2>

      {assessments.map((a) => (
        <div
          key={a.id}
          style={{
            marginBottom: "25px",
            padding: "20px",
            border: "1px solid #ddd",
            borderRadius: "12px",
            background: "#f9f9f9",
          }}
        >
          <h3 style={{ color: "#007bff", marginBottom: "10px" }}>{a.title}</h3>

          {/* âœ… Safe check before accessing sections */}
          {(a.sections || []).map((section, sIndex) => (
            <div key={sIndex} style={{ marginBottom: "15px" }}>
              <h4 style={{ fontWeight: "600", marginBottom: "8px" }}>
                {section.name}
              </h4>

              {/* âœ… Safe check before accessing questions */}
              {(section.questions || []).map((q, qIndex) => (
                <div key={qIndex} style={{ marginBottom: "10px" }}>
                  <label style={{ display: "block", marginBottom: "6px" }}>
                    {q.text}
                  </label>

                  {/* Render input type dynamically */}
                  {q.type === "short" && (
                    <input type="text" style={{ width: "100%", padding: "8px" }} />
                  )}
                  {q.type === "long" && (
                    <textarea style={{ width: "100%", padding: "8px" }} rows="3" />
                  )}
                  {q.type === "numeric" && (
                    <input
                      type="number"
                      min={q.min || 0}
                      max={q.max || 100}
                      style={{ width: "100%", padding: "8px" }}
                    />
                  )}
                  {q.type === "single" &&
                    (q.options || []).map((opt, i) => (
                      <div key={i}>
                        <input type="radio" name={q.text} /> {opt}
                      </div>
                    ))}
                  {q.type === "multi" &&
                    (q.options || []).map((opt, i) => (
                      <div key={i}>
                        <input type="checkbox" /> {opt}
                      </div>
                    ))}
                </div>
              ))}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
