import React, { useEffect, useState } from "react";
import { useAssessmentStore } from "./useAssessmentStore";

export default function Builder() {
  const { assessments, fetchAssessments, addAssessment } = useAssessmentStore();
  const [expanded, setExpanded] = useState(null);
  const [newName, setNewName] = useState("");

  useEffect(() => {
    fetchAssessments();
  }, [fetchAssessments]);

  const toggleExpand = (id) => {
    setExpanded(expanded === id ? null : id);
  };

  const handleAdd = () => {
    if (!newName.trim()) return;
    addAssessment(newName.trim());
    setNewName("");
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Poppins, sans-serif" }}>
      <h2 style={{ fontSize: 22, fontWeight: 600, marginBottom: 20 }}>
        ðŸ§© Assessment Builder
      </h2>

      {/* --- Assessment List --- */}
      {assessments.map((a) => (
        <div
          key={a.id}
          onClick={() => toggleExpand(a.id)}
          style={{
            background: "#edf2ff",
            marginBottom: 12,
            padding: 16,
            borderRadius: 10,
            cursor: "pointer",
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              fontWeight: 600,
              color: "#007bff",
            }}
          >
            {a.title}
            <span>{expanded === a.id ? "â–²" : "â–¼"}</span>
          </div>

          {expanded === a.id && (
            <div style={{ marginTop: 10, paddingLeft: 10 }}>
              {(a.sections || []).map((s, si) => (
                <div key={si}>
                  <h4 style={{ fontWeight: 600, marginBottom: 6 }}>{s.name}</h4>
                  <ul style={{ marginLeft: 15 }}>
                    {(s.questions || []).map((q, qi) => (
                      <li key={qi}>
                        {q.text}{" "}
                        <small style={{ color: "#777" }}>({q.type})</small>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}

      {/* --- Add New Assessment --- */}
      <div style={{ marginTop: 25 }}>
        <input
          type="text"
          placeholder="New Assessment Title"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          style={{
            padding: "8px 12px",
            borderRadius: 6,
            border: "1px solid #ccc",
            marginRight: 8,
          }}
        />
        <button
          onClick={handleAdd}
          style={{
            background: "#007bff",
            color: "#fff",
            border: "none",
            padding: "8px 14px",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          âž• Add
        </button>
      </div>

      {/* --- Live Preview --- */}
      <div style={{ marginTop: 40 }}>
        <h3 style={{ fontSize: 20, fontWeight: 600, marginBottom: 15 }}>
          ðŸ“‹ Live Preview
        </h3>
        {assessments.map((a) => (
          <div
            key={a.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: 10,
              padding: 16,
              marginBottom: 18,
              background: "#fff",
            }}
          >
            <h4 style={{ color: "#007bff", marginBottom: 10 }}>{a.title}</h4>
            {(a.sections || []).map((s, si) => (
              <div key={si}>
                <h5 style={{ marginBottom: 8 }}>{s.name}</h5>
                {(s.questions || []).map((q, qi) => (
                  <div key={qi} style={{ marginBottom: 10 }}>
                    <label>{q.text}</label>
                    <div>
                      {q.type === "short" && <input type="text" />}
                      {q.type === "long" && <textarea rows="2" />}
                      {q.type === "numeric" && <input type="number" />}
                      {q.type === "single" &&
                        q.options?.map((opt, oi) => (
                          <div key={oi}>
                            <input type="radio" name={q.text} /> {opt}
                          </div>
                        ))}
                      {q.type === "multi" &&
                        q.options?.map((opt, oi) => (
                          <div key={oi}>
                            <input type="checkbox" /> {opt}
                          </div>
                        ))}
                    </div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}
